{"output":"using System.Text.Json;
using NorthStarET.NextGen.Lms.Application.Abstractions;
using NorthStarET.NextGen.Lms.Application.Auditing;
using NorthStarET.NextGen.Lms.Application.Districts.Commands;
using NorthStarET.NextGen.Lms.Application.Districts.Models;
using NorthStarET.NextGen.Lms.Application.Districts.Storage;
using NorthStarET.NextGen.Lms.Application.Districts.Validation;
using NorthStarET.NextGen.Lms.Domain.Entities;
using NorthStarET.NextGen.Lms.Domain.Events;

namespace NorthStarET.NextGen.Lms.Application.Districts.Services;

/// <summary>
/// Application service responsible for orchestrating district lifecycle operations.
/// </summary>
public sealed class DistrictService
{
    private readonly IDistrictRepository _districtRepository;
    private readonly IAuditService _auditService;
    private readonly IEventDispatcher _eventDispatcher;

    public DistrictService(
        IDistrictRepository districtRepository,
        IAuditService auditService,
        IEventDispatcher eventDispatcher)
    {
        _districtRepository = districtRepository ?? throw new ArgumentNullException(nameof(districtRepository));
        _auditService = auditService ?? throw new ArgumentNullException(nameof(auditService));
        _eventDispatcher = eventDispatcher ?? throw new ArgumentNullException(nameof(eventDispatcher));
    }

    public async Task<DistrictDto> CreateAsync(CreateDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var name = NormalizeName(command.Name, nameof(command.Name));
        var suffix = NormalizeSuffix(command.DistrictSuffix, nameof(command.DistrictSuffix));

        var suffixInUse = await _districtRepository.IsSuffixInUseAsync(suffix, null, cancellationToken);
        if (suffixInUse)
        {
            throw new DistrictSuffixAlreadyExistsException(suffix);
        }

        var utcNow = DateTime.UtcNow;
        var district = new District
        {
            Id = Guid.NewGuid(),
            Name = name,
            DistrictSuffix = suffix,
            CreatedAt = utcNow,
            UpdatedAt = utcNow
        };

        await _districtRepository.AddAsync(district, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictCreated",
            targetType: "District",
            targetId: district.Id,
            metadataJson: BuildMetadata(new { district.Id, district.Name, district.DistrictSuffix }),
            cancellationToken);

        await _eventDispatcher.PublishAsync(new DistrictCreated
        {
            DistrictId = district.Id,
            DistrictName = district.Name,
            DistrictSuffix = district.DistrictSuffix
        }, cancellationToken);

        return ToDto(district);
    }

    public async Task<DistrictDto> UpdateAsync(UpdateDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var existing = await _districtRepository.GetByIdAsync(command.DistrictId, cancellationToken);
        if (existing is null)
        {
            throw new DistrictNotFoundException(command.DistrictId);
        }

        var name = NormalizeName(command.Name, nameof(command.Name));
        var suffix = NormalizeSuffix(command.DistrictSuffix, nameof(command.DistrictSuffix));

        var suffixInUse = await _districtRepository.IsSuffixInUseAsync(suffix, command.DistrictId, cancellationToken);
        if (suffixInUse)
        {
            throw new DistrictSuffixAlreadyExistsException(suffix);
        }

        existing.Name = name;
        existing.DistrictSuffix = suffix;
        existing.UpdatedAt = DateTime.UtcNow;

        await _districtRepository.UpdateAsync(existing, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictUpdated",
            targetType: "District",
            targetId: existing.Id,
            metadataJson: BuildMetadata(new { existing.Id, existing.Name, existing.DistrictSuffix }),
            cancellationToken);

        await _eventDispatcher.PublishAsync(new DistrictUpdated
        {
            DistrictId = existing.Id,
            DistrictName = existing.Name,
            DistrictSuffix = existing.DistrictSuffix
        }, cancellationToken);

        return ToDto(existing);
    }

    public async Task DeleteAsync(DeleteDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var existing = await _districtRepository.GetByIdAsync(command.DistrictId, cancellationToken);
        if (existing is null)
        {
            throw new DistrictNotFoundException(command.DistrictId);
        }

        await _districtRepository.DeleteAsync(existing, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictDeleted",
            targetType: "District",
            targetId: existing.Id,
            metadataJson: BuildMetadata(new { existing.Id, existing.Name, existing.DistrictSuffix }),
            cancellationToken);

        await _eventDispatcher.PublishAsync(new DistrictDeleted
        {
            DistrictId = existing.Id
        }, cancellationToken);
    }

    public async Task<IReadOnlyList<DistrictDto>> GetAllAsync(CancellationToken cancellationToken)
    {
        var districts = await _districtRepository.GetAllAsync(cancellationToken);
        return districts.Select(ToDto).ToList();
    }

    public async Task<DistrictDto?> GetByIdAsync(Guid districtId, CancellationToken cancellationToken)
    {
        var district = await _districtRepository.GetByIdAsync(districtId, cancellationToken);
        return district is null ? null : ToDto(district);
    }

    private static string NormalizeName(string value, string parameterName)
    {
        if (string.IsNullOrWhiteSpace(value))
        {
            throw new ArgumentException("District name is required.", parameterName);
        }

        return value.Trim();
    }

    private static string NormalizeSuffix(string value, string parameterName)
    {
        if (string.IsNullOrWhiteSpace(value))
        {
            throw new ArgumentException("District suffix is required.", parameterName);
        }

        return value.Trim().ToLowerInvariant();
    }

    private static DistrictDto ToDto(District district) => new(
        district.Id,
        district.Name,
        district.DistrictSuffix,
        district.CreatedAt,
        district.UpdatedAt);

    private static string BuildMetadata(object payload) => JsonSerializer.Serialize(payload);
}
