    Task AddAsync(District district, CancellationToken cancellationToken);

    /// <summary>
    /// Persists changes to an existing district.
    /// </summary>
    Task UpdateAsync(District district, CancellationToken cancellationToken);

    /// <summary>
    /// Removes an existing district.
    /// </summary>
    Task DeleteAsync(District district, CancellationToken cancellationToken);

    /// <summary>
    /// Retrieves a district by its identifier.
    /// </summary>
    Task<District?> GetByIdAsync(Guid districtId, CancellationToken cancellationToken);

