{"output":"using Microsoft.EntityFrameworkCore;
using NorthStarET.NextGen.Lms.Domain.Entities;

namespace NorthStarET.NextGen.Lms.Infrastructure.Persistence;

public class LmsDbContext : DbContext
{
	public LmsDbContext(DbContextOptions<LmsDbContext> options) : base(options) { }

	public DbSet<District> Districts => Set<District>();
	public DbSet<DistrictAdmin> DistrictAdmins => Set<DistrictAdmin>();
	public DbSet<Invite> Invites => Set<Invite>();
	public DbSet<AuditRecord> AuditRecords => Set<AuditRecord>();

	protected override void OnModelCreating(ModelBuilder modelBuilder)
	{
		base.OnModelCreating(modelBuilder);

		modelBuilder.Entity<District>(b =>
		{
			b.HasKey(x => x.Id);
			b.Property(x => x.Name).IsRequired().HasMaxLength(100);
			b.Property(x => x.DistrictSuffix).IsRequired().HasMaxLength(100);
			b.HasIndex(x => x.DistrictSuffix).IsUnique();
			b.Property(x => x.CreatedAt).IsRequired();
			b.Property(x => x.UpdatedAt).IsRequired();
		});

		modelBuilder.Entity<DistrictAdmin>(b =>
		{
			b.HasKey(x => x.Id);
			b.Property(x => x.Email).IsRequired().HasMaxLength(320);
			b.Property(x => x.FirstName).IsRequired().HasMaxLength(100);
			b.Property(x => x.LastName).IsRequired().HasMaxLength(100);
			b.HasOne(x => x.District)
				.WithMany(d => d.Admins)
				.HasForeignKey(x => x.DistrictId)
				.OnDelete(DeleteBehavior.Cascade);
		});

		modelBuilder.Entity<Invite>(b =>
		{
			b.HasKey(x => x.Token);
			b.Property(x => x.Email).IsRequired().HasMaxLength(320);
			b.Property(x => x.IssuedAt).IsRequired();
			b.Property(x => x.ExpiresAt).IsRequired();
			b.Property(x => x.ResendCount).IsRequired();
		});

		modelBuilder.Entity<AuditRecord>(b =>
		{
			b.HasKey(x => x.Id);
			b.Property(x => x.Action).IsRequired().HasMaxLength(200);
			b.Property(x => x.TargetType).IsRequired().HasMaxLength(200);
			b.Property(x => x.Timestamp).IsRequired();
		});
	}
}
