namespace NorthStarET.NextGen.Lms.Infrastructure.Auditing;

/// <summary>
/// Audit service implementation that persists audit records to the database.
/// </summary>
public class AuditService : IAuditService
{
    private readonly LmsDbContext _dbContext;

    public AuditService(LmsDbContext dbContext)
    {
        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
    }

    public async Task RecordAsync(
        Guid actorId,
        string action,
        string targetType,
        Guid targetId,
        string? metadataJson = null,
        CancellationToken cancellationToken = default)
    {
        var auditRecord = new AuditRecord
        {
            Id = Guid.NewGuid(),
            ActorId = actorId,
            Action = action,
            TargetType = targetType,
            TargetId = targetId,
            Timestamp = DateTime.UtcNow,
            MetadataJson = metadataJson
        };

        _dbContext.AuditRecords.Add(auditRecord);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }
}
