namespace NorthStarET.NextGen.Lms.Infrastructure.Messaging;

/// <summary>
/// Simple event dispatcher implementation that logs events.
/// In production, this would integrate with MassTransit or similar message bus.
/// </summary>
public class EventDispatcher : IEventDispatcher
{
    private readonly ILogger<EventDispatcher> _logger;

    public EventDispatcher(ILogger<EventDispatcher> logger)
    {
        _logger = logger ?? throw new ArgumentNullException(nameof(logger));
    }

    public Task PublishAsync<TEvent>(TEvent domainEvent, CancellationToken cancellationToken = default)
        where TEvent : BaseEvent
    {
        if (domainEvent == null)
        {
            throw new ArgumentNullException(nameof(domainEvent));
        }

        // Log the event for now; in production this would publish to a message bus
        _logger.LogInformation(
            "Publishing domain event {EventType} (ID: {EventId}, Version: {Version})",
            typeof(TEvent).Name,
            domainEvent.EventId,
            domainEvent.Version);

        // TODO: Integrate with MassTransit or similar for actual async messaging
        return Task.CompletedTask;
    }
}
