+using Microsoft.EntityFrameworkCore;
+using NorthStarET.NextGen.Lms.Application.Districts.Storage;
+using NorthStarET.NextGen.Lms.Domain.Entities;
+
+namespace NorthStarET.NextGen.Lms.Infrastructure.Persistence.Repositories;
+
+/// <summary>
+/// Entity Framework Core implementation of <see cref="IDistrictRepository"/>.
+/// </summary>
+public sealed class DistrictRepository : IDistrictRepository
+{
+    private readonly LmsDbContext _dbContext;
+
+    public DistrictRepository(LmsDbContext dbContext)
+    {
+        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
+    }
+
+    public async Task<bool> IsSuffixInUseAsync(string normalizedSuffix, Guid? excludingDistrictId, CancellationToken cancellationToken)
+    {
+        ArgumentException.ThrowIfNullOrWhiteSpace(normalizedSuffix);
+
+        var query = _dbContext.Districts.AsQueryable();
+
+        if (excludingDistrictId.HasValue)
+        {
+            query = query.Where(d => d.Id != excludingDistrictId);
+        }
+
+        return await query.AnyAsync(d => d.DistrictSuffix == normalizedSuffix, cancellationToken);
+    }
+
+    public async Task AddAsync(District district, CancellationToken cancellationToken)
+    {
+        ArgumentNullException.ThrowIfNull(district);
+
+        await _dbContext.Districts.AddAsync(district, cancellationToken);
+        await _dbContext.SaveChangesAsync(cancellationToken);
+    }
+
+    public async Task UpdateAsync(District district, CancellationToken cancellationToken)
+    {
+        ArgumentNullException.ThrowIfNull(district);
+
+        _dbContext.Districts.Update(district);
+        await _dbContext.SaveChangesAsync(cancellationToken);
+    }
+
+    public async Task DeleteAsync(District district, CancellationToken cancellationToken)
+    {
+        ArgumentNullException.ThrowIfNull(district);
+
+        _dbContext.Districts.Remove(district);
+        await _dbContext.SaveChangesAsync(cancellationToken);
+    }
+
+    public Task<District?> GetByIdAsync(Guid districtId, CancellationToken cancellationToken)
+    {
+        return _dbContext.Districts
+            .Include(d => d.Admins)
+            .SingleOrDefaultAsync(d => d.Id == districtId, cancellationToken);
+    }
+
+    public async Task<IReadOnlyList<District>> GetAllAsync(CancellationToken cancellationToken)
+    {
+        return await _dbContext.Districts
+            .AsNoTracking()
+            .OrderBy(d => d.Name)
+            .ToListAsync(cancellationToken);
+    }
+}
