+using System.Security.Claims;
+using Microsoft.AspNetCore.Http.HttpResults;
+using Microsoft.AspNetCore.Mvc;
+using NorthStarET.NextGen.Lms.Api.Contracts.Districts;
+using NorthStarET.NextGen.Lms.Application.Districts.Commands;
+using NorthStarET.NextGen.Lms.Application.Districts.Models;
+using NorthStarET.NextGen.Lms.Application.Districts.Services;
+using NorthStarET.NextGen.Lms.Application.Districts.Validation;
+
+namespace NorthStarET.NextGen.Lms.Api.Endpoints;
+
+/// <summary>
+/// Minimal API endpoints for managing districts.
+/// </summary>
+public static class DistrictsEndpoints
+{
+    public static IEndpointRouteBuilder MapDistrictsEndpoints(this IEndpointRouteBuilder endpoints)
+    {
+        var group = endpoints.MapGroup("/api/districts")
+            .WithTags("Districts")
+            .RequireAuthorization("SystemAdmin")
+            .WithOpenApi();
+
+        // GET /api/districts
+        group.MapGet(string.Empty, async Task<Ok<IEnumerable<DistrictResponse>>> (
+            IDistrictService service,
+            CancellationToken cancellationToken) =>
+        {
+            var districts = await service.GetAllAsync(cancellationToken);
+            return TypedResults.Ok(districts.Select(ToResponse));
+        })
+        .WithSummary("List districts")
+        .WithDescription("Retrieves all districts visible to a System Administrator.")
+        .Produces<IEnumerable<DistrictResponse>>(StatusCodes.Status200OK);
+
+        // POST /api/districts
+        group.MapPost(string.Empty, async Task<Results<Created<DistrictResponse>, ValidationProblem, ProblemHttpResult>> (
+            ClaimsPrincipal user,
+            IDistrictService service,
+            CreateDistrictRequest request,
+            CancellationToken cancellationToken) =>
+        {
+            if (!TryResolveActorId(user, out var actorId))
+            {
+                return TypedResults.Problem(
+                    title: "Actor identifier missing",
+                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
+                    statusCode: StatusCodes.Status400BadRequest);
+            }
+
+            try
+            {
+                var command = new CreateDistrictCommand(request.Name, request.DistrictSuffix, actorId);
+                var created = await service.CreateAsync(command, cancellationToken);
+                var response = ToResponse(created);
+                return TypedResults.Created($"/api/districts/{response.Id}", response);
+            }
+            catch (DistrictSuffixAlreadyExistsException ex)
+            {
+                return TypedResults.ValidationProblem(CreateError(nameof(request.DistrictSuffix), ex.Message), statusCode: StatusCodes.Status409Conflict);
+            }
+            catch (ArgumentException ex)
+            {
+                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), statusCode: StatusCodes.Status400BadRequest);
+            }
+        })
+        .WithSummary("Create district")
+        .WithDescription("Creates a new district with a unique, case-insensitive suffix.")
+        .Produces<DistrictResponse>(StatusCodes.Status201Created)
+        .ProducesValidationProblem(StatusCodes.Status400BadRequest)
+        .ProducesValidationProblem(StatusCodes.Status409Conflict);
+
+        // PATCH /api/districts/{id}
+        group.MapPatch("/{id:guid}", async Task<Results<Ok<DistrictResponse>, NotFound, ValidationProblem, ProblemHttpResult>> (
+            ClaimsPrincipal user,
+            Guid id,
+            IDistrictService service,
+            UpdateDistrictRequest request,
+            CancellationToken cancellationToken) =>
+        {
+            if (!TryResolveActorId(user, out var actorId))
+            {
+                return TypedResults.Problem(
+                    title: "Actor identifier missing",
+                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
+                    statusCode: StatusCodes.Status400BadRequest);
+            }
+
+            try
+            {
+                var command = new UpdateDistrictCommand(id, request.Name, request.DistrictSuffix, actorId);
+                var updated = await service.UpdateAsync(command, cancellationToken);
+                return TypedResults.Ok(ToResponse(updated));
+            }
+            catch (DistrictNotFoundException)
+            {
+                return TypedResults.NotFound();
+            }
+            catch (DistrictSuffixAlreadyExistsException ex)
+            {
+                return TypedResults.ValidationProblem(CreateError(nameof(request.DistrictSuffix), ex.Message), statusCode: StatusCodes.Status409Conflict);
+            }
+            catch (ArgumentException ex)
+            {
+                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), statusCode: StatusCodes.Status400BadRequest);
+            }
+        })
+        .WithSummary("Update district")
+        .WithDescription("Updates an existing district's name and suffix.")
+        .Produces<DistrictResponse>(StatusCodes.Status200OK)
+        .Produces(StatusCodes.Status404NotFound)
+        .ProducesValidationProblem(StatusCodes.Status400BadRequest)
+        .ProducesValidationProblem(StatusCodes.Status409Conflict);
+
+        // DELETE /api/districts/{id}
+        group.MapDelete("/{id:guid}", async Task<Results<NoContent, NotFound, ProblemHttpResult>> (
+            ClaimsPrincipal user,
+            Guid id,
+            IDistrictService service,
+            CancellationToken cancellationToken) =>
+        {
+            if (!TryResolveActorId(user, out var actorId))
+            {
+                return TypedResults.Problem(
+                    title: "Actor identifier missing",
+                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
+                    statusCode: StatusCodes.Status400BadRequest);
+            }
+
+            try
+            {
+                var command = new DeleteDistrictCommand(id, actorId);
+                await service.DeleteAsync(command, cancellationToken);
+                return TypedResults.NoContent();
+            }
+            catch (DistrictNotFoundException)
+            {
+                return TypedResults.NotFound();
+            }
+        })
+        .WithSummary("Delete district")
+        .WithDescription("Deletes an existing district.")
+        .Produces(StatusCodes.Status204NoContent)
+        .Produces(StatusCodes.Status404NotFound);
+
+        return endpoints;
+    }
+
+    private static DistrictResponse ToResponse(DistrictDto dto) =>
+        new(dto.Id, dto.Name, dto.DistrictSuffix, dto.CreatedAt, dto.UpdatedAt);
+
+    private static bool TryResolveActorId(ClaimsPrincipal user, out Guid actorId)
+    {
+        var id = user.FindFirstValue("sub")
+            ?? user.FindFirstValue(ClaimTypes.NameIdentifier)
+            ?? user.FindFirstValue("oid");
+
+        return Guid.TryParse(id, out actorId);
+    }
+
+    private static IDictionary<string, string[]> CreateError(string? key, string message)
+    {
+        key ??= string.Empty;
+        return new Dictionary<string, string[]>
+        {
+            [key] = new[] { message }
+        };
+    }
+}
