{"command":["bash","-lc","cat <<'EOF' > src/NorthStarET.NextGen.Lms.Web/Pages/Districts.razor
@page "/districts"
@attribute [Authorize(Policy = "SystemAdmin")]
@using System.Net
@using System.Net.Http.Json
@using System.Text.Json
@using Microsoft.AspNetCore.Authorization
@using Microsoft.AspNetCore.Mvc
@using NorthStarET.NextGen.Lms.UI.Districts.Models
@inject IHttpClientFactory HttpClientFactory
@inject ILogger<Districts> Logger
@inject NavigationManager Navigation

<!-- Figma references: District Management overview (node 22:2), card grid (node 22:31), create district modal (node 22:43) -->

<div class="districts__header">
    <div class="districts__heading">
        <h1>District Management</h1>
        <p>Manage educational districts and their administrators.</p>
    </div>
    <button class="districts__create" @onclick="OpenCreateDialog">
        <span aria-hidden="true">＋</span>
        Create District
    </button>
</div>

@if (!string.IsNullOrWhiteSpace(pageError))
{
    <div class="districts__alert" role="alert">@pageError</div>
}

@if (isLoading)
{
    <p class="districts__loading">Loading districts…</p>
}
else
{
    <DistrictGrid Items="districts"
                  OnEdit="BeginEdit"
                  OnDelete="DeleteDistrictAsync"
                  OnManageAdmins="NavigateToAdmins" />
}

@if (showEditor)
{
    <DistrictEditorForm Model="editorModel"
                        IsEdit="isEdit"
                        IsSubmitting="isSubmitting"
                        ErrorMessage="formError"
                        OnValidSubmit="HandleEditorSubmit"
                        OnCancel="CloseEditor" />
}

@code {
    private readonly List<DistrictListItem> districts = new();
    private DistrictEditorModel editorModel = new();
    private Guid? editingDistrictId;
    private bool isLoading = true;
    private bool showEditor;
    private bool isEdit;
    private bool isSubmitting;
    private string? pageError;
    private string? formError;

    private HttpClient ApiClient => HttpClientFactory.CreateClient("LmsApi");

    protected override async Task OnInitializedAsync()
    {
        await LoadDistrictsAsync();
    }

    private async Task LoadDistrictsAsync()
    {
        isLoading = true;
        pageError = null;
        try
        {
            var response = await ApiClient.GetAsync("/api/districts");
            if (!response.IsSuccessStatusCode)
            {
                pageError = await ExtractErrorAsync(response, "load districts");
                districts.Clear();
                return;
            }

            var payload = await response.Content.ReadFromJsonAsync<List<DistrictResponse>>();
            districts.Clear();
            if (payload is not null)
            {
                districts.AddRange(payload.Select(ToViewModel));
            }
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Failed to load districts");
            pageError = "We couldn't load districts right now. Please try again.";
        }
        finally
        {
            isLoading = false;
            StateHasChanged();
        }
    }

    private void OpenCreateDialog()
    {
        editorModel = new DistrictEditorModel();
        editingDistrictId = null;
        isEdit = false;
        formError = null;
        showEditor = true;
    }

    private void BeginEdit(DistrictListItem district)
    {
        editorModel = DistrictEditorModel.From(district);
        editingDistrictId = district.Id;
        isEdit = true;
        formError = null;
        showEditor = true;
    }

    private async Task HandleEditorSubmit(DistrictEditorModel model)
    {
        if (isSubmitting)
        {
            return;
        }

        isSubmitting = true;
        formError = null;

        try
        {
            HttpResponseMessage response;
            if (isEdit && editingDistrictId.HasValue)
            {
                var request = new HttpRequestMessage(HttpMethod.Patch, $"/api/districts/{editingDistrictId}")
                {
                    Content = JsonContent.Create(new UpdateDistrictRequest(model.Name, model.DistrictSuffix))
                };
                response = await ApiClient.SendAsync(request);
            }
            else
            {
                response = await ApiClient.PostAsJsonAsync("/api/districts", new CreateDistrictRequest(model.Name, model.DistrictSuffix));
            }

            if (!response.IsSuccessStatusCode)
            {
                formError = await ExtractErrorAsync(response, isEdit ? "update district" : "create district");
                return;
            }

            DistrictResponse? payload;
            if (response.StatusCode == HttpStatusCode.NoContent)
            {
                payload = null;
            }
            else
            {
                payload = await response.Content.ReadFromJsonAsync<DistrictResponse>();
            }

            if (payload is not null)
            {
                var viewModel = ToViewModel(payload);
                var existingIndex = districts.FindIndex(d => d.Id == viewModel.Id);
                if (existingIndex >= 0)
                {
                    districts[existingIndex] = viewModel;
                }
                else
                {
                    districts.Add(viewModel);
                }
            }
            else
            {
                await LoadDistrictsAsync();
            }

            CloseEditor();
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Failed to {Operation} district", isEdit ? "update" : "create");
            formError = "Something went wrong while saving. Please try again.";
        }
        finally
        {
            isSubmitting = false;
        }
    }

    private async Task DeleteDistrictAsync(DistrictListItem district)
    {
        try
        {
            var response = await ApiClient.DeleteAsync($"/api/districts/{district.Id}");
            if (!response.IsSuccessStatusCode)
            {
                pageError = await ExtractErrorAsync(response, "delete district");
                return;
            }

            districts.RemoveAll(d => d.Id == district.Id);
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Failed to delete district {DistrictId}", district.Id);
            pageError = "Failed to delete district. Please try again.";
        }
    }

    private void NavigateToAdmins(DistrictListItem district)
    {
        Navigation.NavigateTo($"/districts/{district.Id}/admins");
    }

    private void CloseEditor()
    {
        showEditor = false;
        isSubmitting = false;
        formError = null;
        editingDistrictId = null;
    }

    private static DistrictListItem ToViewModel(DistrictResponse response) =>
        new(response.Id, response.Name, response.DistrictSuffix, response.CreatedAt, response.UpdatedAt);

    private static async Task<string> ExtractErrorAsync(HttpResponseMessage response, string action)
    {
        var content = await response.Content.ReadAsStringAsync();
        if (string.IsNullOrWhiteSpace(content))
        {
            return $"Failed to {action}. ({(int)response.StatusCode} {response.StatusCode})";
        }

        try
        {
            using var document = JsonDocument.Parse(content);
            if (document.RootElement.TryGetProperty("errors", out var errorsElement) && errorsElement.ValueKind == JsonValueKind.Object)
            {
                var messages = new List<string>();
                foreach (var property in errorsElement.EnumerateObject())
                {
                    foreach (var error in property.Value.EnumerateArray())
                    {
                        messages.Add(error.GetString() ?? string.Empty);
                    }
                }

                if (messages.Count > 0)
                {
                    return string.Join(" ", messages);
                }
            }

            if (document.RootElement.TryGetProperty("detail", out var detail) && detail.ValueKind == JsonValueKind.String)
            {
                return detail.GetString() ?? string.Empty;
            }

            if (document.RootElement.TryGetProperty("title", out var title) && title.ValueKind == JsonValueKind.String)
            {
                return title.GetString() ?? string.Empty;
            }
        }
        catch (JsonException)
        {
            // Fall back to raw content below.
        }

        return content;
    }

    private sealed record DistrictResponse(Guid Id, string Name, string DistrictSuffix, DateTime CreatedAt, DateTime UpdatedAt);
    private sealed record CreateDistrictRequest(string Name, string DistrictSuffix);
    private sealed record UpdateDistrictRequest(string Name, string DistrictSuffix);
}
